         "description": "Settings that control how the Kiali server itself emits its own tracing data.",
         "properties": {
          "collector_type": {
           "default": "otel",
           "description": "The collector type to use. Today the only valid value is `otel`.",
           "enum": [
            "otel"
           ],
           "type": "string"
          },
          "collector_url": {
           "default": "jaeger-collector.istio-system:4318",
           "description": "Usd to determine where the Kiali server tracing data will be stored.",
           "type": "string"
          },
          "enabled": {
           "default": false,
           "description": "When true, the Kiali server itself will product its own tracing data.",
           "type": "boolean"
          },
