schema:
{
 "description": "KommanderClusterDiagnosis is the Schema for the kommander clusters diagnosis API.",
 "properties": {
  "spec": {
   "description": "KommanderClusterSpec defines the desired state of Cluster.",
   "properties": {
    "deletePolicy": {
     "description": "DeletePolicy specifies if a storage location is deleted when the KommanderClusterDiagnosis resourece is deleted. (default: `Delete`)",
     "type": "string"
    },
    "kommanderClusterRef": {
     "description": "KommanderClusterRef references the KommanderCluster for which the diagnostic bundle is created.",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    },
    "objectStorage": {
     "description": "ObjectStorage configures the object storage to be used for the diagnostic bundle.",
     "properties": {
      "bucket": {
       "description": "Name of bucket to be used to store the created diagnostic bundle.",
       "type": "string"
      },
      "cloudProviderAccountRef": {
       "description": "CloudProviderAccount references credentials for a provider used to to provision the object storage. (For now only aws)",
       "properties": {
        "name": {
         "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "bucket",
      "cloudProviderAccountRef"
     ],
     "type": "object"
    }
   },
   "required": [
    "kommanderClusterRef",
    "objectStorage"
   ],
   "type": "object"
  }
 },
 "title": "Kommander Cluster Diagnosis",
 "type": "object"
}
