schema:
{
 "description": "Project is a logical top-level container for a set of Kommander resources.",
 "properties": {
  "spec": {
   "description": "ProjectSpec describes the attributes on a Project.",
   "properties": {
    "namespaceName": {
     "description": "NamespaceName specifies the optional namespace name to use for the project. This field is immutable, only settable on create.",
     "type": "string"
    },
    "placement": {
     "description": "PlacementSelector defines the fields to select clusters where to apply the project.",
     "properties": {
      "clusterSelector": {
       "description": "A label selector is a label query over a set of resources. The result of matchLabels and matchExpressions are ANDed. An empty label selector matches all objects. A null label selector matches no objects.",
       "properties": {
        "matchExpressions": {
         "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
         "items": {
          "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
          "properties": {
           "key": {
            "description": "key is the label key that the selector applies to.",
            "type": "string"
           },
           "operator": {
            "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
            "type": "string"
           },
           "values": {
            "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
            "items": {
             "type": "string"
            },
            "type": "array"
           }
          },
          "required": [
           "key",
           "operator"
          ],
          "type": "object"
         },
         "type": "array"
        },
        "matchLabels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
         "type": "object"
        }
       },
       "type": "object"
      },
      "clusters": {
       "items": {
        "description": "GenericClusterReference sets the name of the cluster.",
        "properties": {
         "name": {
          "type": "string"
         }
        },
        "required": [
         "name"
        ],
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "workspaceRef": {
     "description": "LocalObjectReference contains enough information to let you locate the referenced object inside the same namespace.",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names TODO: Add other useful fields. apiVersion, kind, uid?",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Project",
 "type": "object"
}
