component.schema:
{
 "description": "Describes how various services (e.g., tunner server) would be exposed outside the current cluster.",
 "properties": {
  "spec": {
   "properties": {
    "ingress": {
     "description": "If specified, services will be exposed using Ingress.",
     "properties": {
      "caSecretRef": {
       "description": "A secret reference to the root CA that will be used to validate the ingress endpoints. The secret should have type \"Opaque\" and contain the key \"tls.crt\". If not specified, the default host root CA will be used to validate the endpoints.",
       "properties": {
        "apiVersion": {
         "description": "API version of the referent.",
         "type": "string"
        },
        "fieldPath": {
         "description": "If referring to a piece of an object instead of an entire object, this string should contain a valid JSON/Go field access statement, such as desiredState.manifest.containers[2]. For example, if the object reference is to a container within a pod, this would take on a value like: \"spec.containers{name}\" (where \"name\" refers to the name of the container that triggered the event) or if no container name is specified \"spec.containers[2]\" (container with index 2 in this pod). This syntax is chosen only to have some well-defined way of referencing a part of an object. TODO: this design is not final and this field is subject to change in the future.",
         "type": "string"
        },
        "kind": {
         "description": "Kind of the referent. More info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#types-kinds",
         "type": "string"
        },
        "name": {
         "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
         "type": "string"
        },
        "namespace": {
         "description": "Namespace of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/namespaces/",
         "type": "string"
        },
        "resourceVersion": {
         "description": "Specific resourceVersion to which this reference is made, if any. More info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#concurrency-control-and-consistency",
         "type": "string"
        },
        "uid": {
         "description": "UID of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#uids",
         "type": "string"
        }
       },
       "type": "object"
      },
      "extraAnnotations": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Extra annotations to set on the Ingress object.",
       "type": "object"
      },
      "host": {
       "description": "Host in the Ingress record.",
       "type": "string"
      },
      "loadBalancer": {
       "description": "Ingress points for the load-balancer. Traffic intended for the service should be sent to these ingress points. If not specified, the controller will try to derive that from the Ingress record status field.",
       "properties": {
        "hostname": {
         "description": "Hostname is set for load-balancer ingress points that are DNS based (typically AWS load-balancers)",
         "type": "string"
        },
        "ip": {
         "description": "IP is set for load-balancer ingress points that are IP based (typically GCE or OpenStack load-balancers)",
         "type": "string"
        },
        "ports": {
         "description": "Ports is a list of records of service ports If used, every port defined in the service should have an entry in it",
         "items": {
          "properties": {
           "error": {
            "description": "Error is to record the problem with the service port The format of the error shall comply with the following rules: - built-in error values shall be specified in this file and those shall use   CamelCase names - cloud provider specific error values must have names that comply with the   format foo.example.com/CamelCase. --- The regex it matches is (dns1123SubdomainFmt/)?(qualifiedNameFmt)",
            "maxLength": 316,
            "pattern": "^([a-z0-9]([-a-z0-9]*[a-z0-9])?(\\.[a-z0-9]([-a-z0-9]*[a-z0-9])?)*/)?(([A-Za-z0-9][-A-Za-z0-9_.]*)?[A-Za-z0-9])$",
            "type": "string"
           },
           "port": {
            "description": "Port is the port number of the service port of which status is recorded here",
            "format": "int32",
            "type": "integer"
           },
           "protocol": {
            "description": "Protocol is the protocol of the service port of which status is recorded here The supported values are: \"TCP\", \"UDP\", \"SCTP\"",
            "type": "string"
           }
          },
          "required": [
           "port",
           "protocol"
          ],
          "type": "object"
         },
         "type": "array",
         "x-kubernetes-list-type": "atomic"
        }
       },
       "type": "object"
      },
      "urlPathPrefix": {
       "description": "URL path prefix to be append to all Ingresses. For example, if this field is set to \"/ops/portal/kt\", the ingresses created will have URL paths like the following:   \"/ops/portal/kt/default/cluster1/tunnel-server\"   \"/ops/portal/kt/default/cluster1/kubeconfig\" If not specified, default to root path (i.e., \"/\").",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Tunnel Gateway",
 "type": "object"
}
