component.schema:
{
 "properties": {
  "spec": {
   "description": "Spec is the specification of the Kuma MeshIdentity resource.",
   "properties": {
    "provider": {
     "properties": {
      "bundled": {
       "description": "Bundled provides information about certificates that are generated by the control plane,\neither autogenerated or provided by the user.",
       "properties": {
        "autogenerate": {
         "description": "Autogenerate configures the control plane to use self-signed certificates.",
         "properties": {
          "enabled": {
           "type": "boolean"
          }
         },
         "type": "object"
        },
        "ca": {
         "description": "CA has configuration related to the CA",
         "properties": {
          "certificate": {
           "description": "Certificate allows the user to specify a custom certificate.",
           "properties": {
            "envVar": {
             "properties": {
              "name": {
               "type": "string"
              }
             },
             "required": [
              "name"
             ],
             "type": "object"
            },
            "file": {
             "properties": {
              "path": {
               "type": "string"
              }
             },
             "required": [
              "path"
             ],
             "type": "object"
            },
            "insecureInline": {
             "properties": {
              "value": {
               "type": "string"
              }
             },
             "required": [
              "value"
             ],
             "type": "object"
            },
            "secretRef": {
             "properties": {
              "kind": {
               "enum": [
                "Secret"
               ],
               "type": "string"
              },
              "name": {
               "type": "string"
              }
             },
             "required": [
              "kind",
              "name"
             ],
             "type": "object"
            },
            "type": {
             "enum": [
              "File",
              "Secret",
              "EnvVar",
              "InsecureInline"
             ],
             "type": "string"
            }
           },
           "required": [
            "type"
           ],
           "type": "object"
          },
          "privateKey": {
           "description": "PrivateKey allows the user to specify a custom private key.",
           "properties": {
            "envVar": {
             "properties": {
              "name": {
               "type": "string"
              }
             },
             "required": [
              "name"
             ],
             "type": "object"
            },
            "file": {
             "properties": {
              "path": {
               "type": "string"
              }
             },
             "required": [
              "path"
             ],
             "type": "object"
            },
            "insecureInline": {
             "properties": {
              "value": {
               "type": "string"
              }
             },
             "required": [
              "value"
             ],
             "type": "object"
            },
            "secretRef": {
             "properties": {
              "kind": {
               "enum": [
                "Secret"
               ],
               "type": "string"
              },
              "name": {
               "type": "string"
              }
             },
             "required": [
              "kind",
              "name"
             ],
             "type": "object"
            },
            "type": {
             "enum": [
              "File",
              "Secret",
              "EnvVar",
              "InsecureInline"
             ],
             "type": "string"
            }
           },
           "required": [
            "type"
           ],
           "type": "object"
          }
         },
         "type": "object"
        },
        "certificateParameters": {
         "description": "CertificateParameters allows users to define certificate generation parameters.",
         "properties": {
          "expiry": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "insecureAllowSelfSigned": {
         "description": "InsecureAllowSelfSigned allows users to enable the use of self-signed certificates.",
         "type": "boolean"
        },
        "meshTrustCreation": {
         "description": "MeshTrustCreation defines whether a MeshTrust resource should be automatically created\nfrom an existing MeshIdentity. If not defined, the control plane automatically generates a MeshTrust.",
         "enum": [
          "Enabled",
          "Disabled"
         ],
         "type": "string"
        }
       },
       "type": "object"
      },
      "spire": {
       "description": "Spire indicates that SPIRE is used for certificate delivery.",
       "properties": {
        "agent": {
         "description": "Spire agent configuration",
         "properties": {
          "timeout": {
           "description": "Connection timeout to the socket exposed by Spire agent\nDefault 1 second.",
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "type": {
       "description": "Type specifies the type of certificate provider.",
       "enum": [
        "Bundled",
        "Spire"
       ],
       "type": "string"
      }
     },
     "required": [
      "type"
     ],
     "type": "object"
    },
    "selector": {
     "properties": {
      "dataplane": {
       "properties": {
        "matchLabels": {
         "additionalProperties": {
          "type": "string"
         },
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "spiffeID": {
     "properties": {
      "path": {
       "type": "string"
      },
      "trustDomain": {
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "provider"
   ],
   "type": "object"
  }
 },
 "title": "Mesh Identity",
 "type": "object"
}
