schema:
{
 "description": "APIService is a special resource used in Ks-apiserver declares a directional proxy path for a resource type APIï¼Œ it's similar to Kubernetes API Aggregation Layer.",
 "properties": {
  "spec": {
   "properties": {
    "caBundle": {
     "format": "byte",
     "type": "string"
    },
    "group": {
     "type": "string"
    },
    "insecureSkipVerify": {
     "type": "boolean"
    },
    "service": {
     "description": "service is a reference to the service for this endpoint. Either service or url must be specified. the scheme is default to HTTPS.",
     "properties": {
      "name": {
       "description": "name is the name of the service. Required",
       "type": "string"
      },
      "namespace": {
       "description": "namespace is the namespace of the service. Required",
       "type": "string"
      },
      "path": {
       "description": "path is an optional URL path at which the upstream will be contacted.",
       "type": "string"
      },
      "port": {
       "description": "port is an optional service port at which the upstream will be contacted. `port` should be a valid port number (1-65535, inclusive). Defaults to 443 for backward compatibility.",
       "format": "int32",
       "type": "integer"
      }
     },
     "required": [
      "name",
      "namespace"
     ],
     "type": "object"
    },
    "url": {
     "description": "`url` gives the location of the upstream, in standard URL form (`scheme://host:port/path`). Exactly one of `url` or `service` must be specified.",
     "type": "string"
    },
    "version": {
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "API Service",
 "type": "object"
}
