      "command": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "configMaps": {
       "items": {
        "minProperties": 2,
        "properties": {
         "mountPath": {
          "allowEmptyValue": false,
          "minLength": 1,
          "type": "string"
         },
         "name": {
          "allowEmptyValue": false,
          "minLength": 1,
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "env": {
       "items": {
        "properties": {
         "name": {
          "type": "string"
         },
         "value": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "hostFileVolumes": {
       "items": {
        "minProperties": 3,
        "properties": {
         "mountPath": {
          "allowEmptyValue": false,
          "minLength": 1,
          "type": "string"
         },
         "name": {
          "allowEmptyValue": false,
          "minLength": 1,
          "type": "string"
         },
         "nodePath": {
          "allowEmptyValue": false,
          "minLength": 1,
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "hostPID": {
       "type": "boolean"
      },
      "image": {
       "type": "string"
      },
      "imagePullPolicy": {
       "type": "string"
      },
      "labels": {
       "additionalProperties": {
        "type": "string"
       },
       "type": "object"
      },
      "permissions": {
       "items": {
        "minProperties": 3,
        "properties": {
         "apiGroups": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "nonResourceURLs": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "resourceNames": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "resources": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "verbs": {
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "required": [
         "apiGroups",
         "resources",
         "verbs"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "scope": {
       "pattern": "^(Namespaced|Cluster)$",
       "type": "string"
      },
      "secrets": {
       "items": {
        "minProperties": 2,
        "properties": {
         "mountPath": {
          "allowEmptyValue": false,
          "minLength": 1,
          "type": "string"
         },
         "name": {
          "allowEmptyValue": false,
          "minLength": 1,
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "securityContext": {
       "format": "textarea",
       "type": "string"
      }
     },
     "type": "string"
    }
   },
   "type": "object"
  }
