component.schema:
{
 "description": "AWSECRCredential is the Schema for the awsecrcredentials API. It manages several docker secrets for AWS ECR across different namespaces.",
 "properties": {
  "spec": {
   "description": "AWSECRCredentialSpec defines the desired state of AWSECRCredential",
   "properties": {
    "awsAccess": {
     "description": "the name of the secret holding the AWS credentials that will be used to talk to AWS to get ECR credentials",
     "properties": {
      "namespace": {
       "default": "default",
       "description": "the namespace of the secret",
       "type": "string"
      },
      "secretName": {
       "description": "the name of the secret holding the AWS credentials that will be used to talk to AWS to get ECR credentials",
       "maxLength": 253,
       "minLength": 1,
       "type": "string"
      }
     },
     "type": "object"
    },
    "namespaces": {
     "description": "the namespaces in which the operator will create and and manage ECR registry docker secrets",
     "items": {
      "type": "string"
     },
     "minItems": 1,
     "type": "array"
    },
    "secretName": {
     "description": "the name of the docker secret that will be created and updated by the operator in each of the specified namespaces",
     "maxLength": 253,
     "minLength": 1,
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "AWSECR Credential",
 "type": "object"
}
