component.schema:
{
 "properties": {
  "spec": {
   "properties": {
    "chart": {
     "properties": {
      "metadata": {
       "properties": {
        "annotations": {
         "additionalProperties": {
          "type": "string"
         },
         "type": "object"
        },
        "labels": {
         "additionalProperties": {
          "type": "string"
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "spec": {
       "properties": {
        "chart": {
         "maxLength": 2048,
         "minLength": 1,
         "type": "string"
        },
        "ignoreMissingValuesFiles": {
         "type": "boolean"
        },
        "interval": {
         "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m|h))+$",
         "type": "string"
        },
        "reconcileStrategy": {
         "default": "ChartVersion",
         "enum": [
          "ChartVersion",
          "Revision"
         ],
         "type": "string"
        },
        "sourceRef": {
         "properties": {
          "apiVersion": {
           "type": "string"
          },
          "kind": {
           "enum": [
            "HelmRepository",
            "GitRepository",
            "Bucket"
           ],
           "type": "string"
          },
          "name": {
           "maxLength": 253,
           "minLength": 1,
           "type": "string"
          },
          "namespace": {
           "maxLength": 63,
           "minLength": 1,
           "type": "string"
          }
         },
         "required": [
          "kind",
          "name"
         ],
         "type": "object"
        },
        "valuesFiles": {
         "items": {
          "type": "string"
         },
         "type": "array"
        },
        "verify": {
         "properties": {
          "provider": {
           "default": "cosign",
           "enum": [
            "cosign",
            "notation"
           ],
           "type": "string"
          },
          "secretRef": {
           "properties": {
            "name": {
             "type": "string"
            }
           },
           "required": [
            "name"
           ],
           "type": "object"
          }
         },
         "required": [
          "provider"
         ],
         "type": "object"
        },
        "version": {
         "default": "*",
         "type": "string"
        }
       },
       "required": [
        "chart",
        "sourceRef"
       ],
       "type": "object"
      }
     },
     "required": [
      "spec"
     ],
     "type": "object"
    },
    "chartRef": {
     "properties": {
      "apiVersion": {
       "type": "string"
      },
      "kind": {
       "enum": [
        "OCIRepository",
        "HelmChart"
       ],
       "type": "string"
      },
      "name": {
       "maxLength": 253,
       "minLength": 1,
       "type": "string"
      },
      "namespace": {
       "maxLength": 63,
       "minLength": 1,
       "type": "string"
      }
     },
     "required": [
      "kind",
      "name"
     ],
     "type": "object"
    },
    "dependsOn": {
     "items": {
      "properties": {
       "name": {
        "type": "string"
       },
       "namespace": {
        "type": "string"
       }
      },
      "required": [
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "driftDetection": {
     "properties": {
      "ignore": {
       "items": {
        "properties": {
         "paths": {
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "target": {
          "properties": {
           "annotationSelector": {
            "type": "string"
           },
           "group": {
            "type": "string"
           },
           "kind": {
            "type": "string"
           },
           "labelSelector": {
            "type": "string"
           },
           "name": {
            "type": "string"
           },
           "namespace": {
            "type": "string"
           },
           "version": {
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "required": [
         "paths"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "mode": {
       "enum": [
        "enabled",
        "warn",
        "disabled"
       ],
       "type": "string"
      }
     },
     "type": "object"
    },
    "install": {
     "properties": {
      "crds": {
       "enum": [
        "Skip",
        "Create",
        "CreateReplace"
       ],
       "type": "string"
      },
      "createNamespace": {
       "type": "boolean"
      },
      "disableHooks": {
       "type": "boolean"
      },
      "disableOpenAPIValidation": {
       "type": "boolean"
      },
      "disableSchemaValidation": {
       "type": "boolean"
      },
      "disableTakeOwnership": {
       "type": "boolean"
      },
      "disableWait": {
       "type": "boolean"
      },
      "disableWaitForJobs": {
       "type": "boolean"
      },
      "remediation": {
       "properties": {
        "ignoreTestFailures": {
         "type": "boolean"
        },
        "remediateLastFailure": {
         "type": "boolean"
        },
        "retries": {
         "type": "integer"
        }
       },
       "type": "object"
      },
      "replace": {
       "type": "boolean"
      },
      "skipCRDs": {
       "type": "boolean"
      },
      "timeout": {
       "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m|h))+$",
       "type": "string"
      }
     },
     "type": "object"
    },
    "interval": {
     "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m|h))+$",
     "type": "string"
    },
    "kubeConfig": {
     "properties": {
      "secretRef": {
       "properties": {
        "key": {
         "type": "string"
        },
        "name": {
         "type": "string"
        }
       },
       "required": [
        "name"
       ],
       "type": "object"
      }
     },
     "required": [
      "secretRef"
     ],
     "type": "object"
    },
    "maxHistory": {
     "type": "integer"
    },
    "persistentClient": {
     "type": "boolean"
    },
    "postRenderers": {
     "items": {
      "properties": {
       "kustomize": {
        "properties": {
         "images": {
          "items": {
           "properties": {
            "digest": {
             "type": "string"
            },
            "name": {
             "type": "string"
            },
            "newName": {
             "type": "string"
            },
            "newTag": {
             "type": "string"
            }
           },
           "required": [
            "name"
           ],
           "type": "object"
          },
          "type": "array"
         },
         "patches": {
          "items": {
           "properties": {
            "patch": {
             "type": "string"
            },
            "target": {
             "properties": {
              "annotationSelector": {
               "type": "string"
              },
              "group": {
               "type": "string"
              },
              "kind": {
               "type": "string"
              },
              "labelSelector": {
               "type": "string"
              },
              "name": {
               "type": "string"
              },
              "namespace": {
               "type": "string"
              },
              "version": {
               "type": "string"
              }
             },
             "type": "object"
            }
           },
           "required": [
            "patch"
           ],
           "type": "object"
          },
          "type": "array"
         }
        },
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "releaseName": {
     "maxLength": 53,
     "minLength": 1,
     "type": "string"
    },
    "rollback": {
     "properties": {
      "cleanupOnFail": {
       "type": "boolean"
      },
      "disableHooks": {
       "type": "boolean"
      },
      "disableWait": {
       "type": "boolean"
      },
      "disableWaitForJobs": {
       "type": "boolean"
      },
      "force": {
       "type": "boolean"
      },
      "recreate": {
       "type": "boolean"
      },
      "timeout": {
       "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m|h))+$",
       "type": "string"
      }
     },
     "type": "object"
    },
    "serviceAccountName": {
     "maxLength": 253,
     "minLength": 1,
     "type": "string"
    },
    "storageNamespace": {
     "maxLength": 63,
     "minLength": 1,
     "type": "string"
    },
    "suspend": {
     "type": "boolean"
    },
    "targetNamespace": {
     "maxLength": 63,
     "minLength": 1,
     "type": "string"
    },
    "test": {
     "properties": {
      "enable": {
       "type": "boolean"
      },
      "filters": {
       "items": {
        "properties": {
         "exclude": {
          "type": "boolean"
         },
         "name": {
          "maxLength": 253,
          "minLength": 1,
          "type": "string"
         }
        },
        "required": [
         "name"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "ignoreFailures": {
       "type": "boolean"
      },
      "timeout": {
       "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m|h))+$",
       "type": "string"
      }
     },
     "type": "object"
    },
    "timeout": {
     "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m|h))+$",
     "type": "string"
    },
    "uninstall": {
     "properties": {
      "deletionPropagation": {
       "default": "background",
       "enum": [
        "background",
        "foreground",
        "orphan"
       ],
       "type": "string"
      },
      "disableHooks": {
       "type": "boolean"
      },
      "disableWait": {
       "type": "boolean"
      },
      "keepHistory": {
       "type": "boolean"
      },
      "timeout": {
       "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m|h))+$",
       "type": "string"
      }
     },
     "type": "object"
    },
    "upgrade": {
     "properties": {
      "cleanupOnFail": {
       "type": "boolean"
      },
      "crds": {
       "enum": [
        "Skip",
        "Create",
        "CreateReplace"
       ],
       "type": "string"
      },
      "disableHooks": {
       "type": "boolean"
      },
      "disableOpenAPIValidation": {
       "type": "boolean"
      },
      "disableSchemaValidation": {
       "type": "boolean"
      },
      "disableTakeOwnership": {
       "type": "boolean"
      },
      "disableWait": {
       "type": "boolean"
      },
      "disableWaitForJobs": {
       "type": "boolean"
      },
      "force": {
       "type": "boolean"
      },
      "preserveValues": {
       "type": "boolean"
      },
      "remediation": {
       "properties": {
        "ignoreTestFailures": {
         "type": "boolean"
        },
        "remediateLastFailure": {
         "type": "boolean"
        },
        "retries": {
         "type": "integer"
        },
        "strategy": {
         "enum": [
          "rollback",
          "uninstall"
         ],
         "type": "string"
        }
       },
       "type": "object"
      },
      "timeout": {
       "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m|h))+$",
       "type": "string"
      }
     },
     "type": "object"
    },
    "values": {
     "format": "textarea",
     "type": "string"
    },
    "valuesFrom": {
     "items": {
      "properties": {
       "kind": {
        "enum": [
         "Secret",
         "ConfigMap"
        ],
        "type": "string"
       },
       "name": {
        "maxLength": 253,
        "minLength": 1,
        "type": "string"
       },
       "optional": {
        "type": "boolean"
       },
       "targetPath": {
        "maxLength": 250,
        "pattern": "^([a-zA-Z0-9_\\-.\\\\\\/]|\\[[0-9]{1,5}\\])+$",
        "type": "string"
       },
       "valuesKey": {
        "maxLength": 253,
        "pattern": "^[\\-._a-zA-Z0-9]+$",
        "type": "string"
       }
      },
      "required": [
       "kind",
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "interval"
   ],
   "type": "object",
   "x-kubernetes-validations": [
    {
     "message": "either chart or chartRef must be set",
     "rule": "(has(self.chart) \u0026\u0026 !has(self.chartRef)) || (!has(self.chart) \u0026\u0026 has(self.chartRef))"
    }
   ]
  }
 },
 "title": "Helm Release",
 "type": "object"
}
