schema:
{
 "properties": {
  "spec": {
   "description": "ReplicaSchedulingPreferenceSpec defines the desired state of ReplicaSchedulingPreference",
   "properties": {
    "clusters": {
     "additionalProperties": {
      "description": "Preferences regarding number of replicas assigned to a cluster workload object (dep, rs, ..) within a federated workload object.",
      "properties": {
       "maxReplicas": {
        "description": "Maximum number of replicas that should be assigned to this cluster workload object. Unbounded if no value provided (default).",
        "format": "int64",
        "type": "integer"
       },
       "minReplicas": {
        "description": "Minimum number of replicas that should be assigned to this cluster workload object. 0 by default.",
        "format": "int64",
        "type": "integer"
       },
       "weight": {
        "description": "A number expressing the preference to put an additional replica to this cluster workload object. 0 by default.",
        "format": "int64",
        "type": "integer"
       }
      },
      "type": "object"
     },
     "description": "A mapping between cluster names and preferences regarding a local workload object (dep, rs, .. ) in these clusters. \"*\" (if provided) applies to all clusters if an explicit mapping is not provided. If omitted, clusters without explicit preferences should not have any replicas scheduled.",
     "type": "object"
    },
    "intersectWithClusterSelector": {
     "description": "If set to true, the placement of target kind will be determined using the instersection of RSP placement scheduling result and the clusterSelector (spec.placement.clusterSelector) specified on the target kind. If set to false or not defined, RSP placement scheduling result overwrites the clusters list in the spec.placement.clusters of the target resource.",
     "type": "boolean"
    },
    "rebalance": {
     "description": "If set to true then already scheduled and running replicas may be moved to other clusters in order to match current state to the specified preferences. Otherwise, if set to false, up and running replicas will not be moved.",
     "type": "boolean"
    },
    "targetKind": {
     "description": "TODO (@irfanurrehman); upgrade this to label selector only if need be. The idea of this API is to have a a set of preferences which can be used for a target FederatedDeployment or FederatedReplicaset. Although the set of preferences in question can be applied to multiple target objects using label selectors, but there are no clear advantages of doing that as of now. To keep the implementation and usage simple, matching ns/name of RSP resource to the target resource is sufficient and only additional information needed in RSP resource is a target kind (FederatedDeployment or FederatedReplicaset).",
     "type": "string"
    },
    "totalReplicas": {
     "description": "Total number of pods desired across federated clusters. Replicas specified in the spec for target deployment template or replicaset template will be discarded/overridden when scheduling preferences are specified.",
     "format": "int32",
     "type": "integer"
    }
   },
   "required": [
    "targetKind",
    "totalReplicas"
   ],
   "type": "object"
  }
 },
 "title": "Replica Scheduling Preference",
 "type": "object"
}
