schema:
{
 "description": "Domain is the Schema for the Domains API.",
 "properties": {
  "spec": {
   "description": "DomainSpec defines the desired state of Domain",
   "properties": {
    "deletionPolicy": {
     "default": "Delete",
     "description": "DeletionPolicy specifies what will happen to the underlying external when this managed resource is deleted - either \"Delete\" or \"Orphan\" the external resource. This field is planned to be deprecated in favor of the ManagementPolicy field in a future release. Currently, both could be set independently and non-default values would be honored if the feature flag is enabled. See the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223",
     "enum": [
      "Orphan",
      "Delete"
     ],
     "type": "string"
    },
    "forProvider": {
     "properties": {
      "accessPolicies": {
       "description": "IAM policy document specifying the access policies for the domain.",
       "type": "string"
      },
      "advancedOptions": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Key-value string pairs to specify advanced configuration options.",
       "type": "object"
      },
      "advancedSecurityOptions": {
       "description": "Configuration block for fine-grained access control. Detailed below.",
       "items": {
        "properties": {
         "enabled": {
          "description": "Whether advanced security is enabled.",
          "type": "boolean"
         },
         "internalUserDatabaseEnabled": {
          "description": "Whether the internal user database is enabled. If not set, defaults to false by the AWS API.",
          "type": "boolean"
         },
         "masterUserOptions": {
          "description": "Configuration block for the main user. Detailed below.",
          "items": {
           "properties": {
            "masterUserArn": {
             "description": "ARN for the main user. Only specify if internal_user_database_enabled is not set or set to false.",
             "type": "string"
            },
            "masterUserName": {
             "description": "Main user's username, which is stored in the Amazon Elasticsearch Service domain's internal database. Only specify if internal_user_database_enabled is set to true.",
             "type": "string"
            },
            "masterUserPasswordSecretRef": {
             "description": "Main user's password, which is stored in the Amazon Elasticsearch Service domain's internal database. Only specify if internal_user_database_enabled is set to true.",
             "properties": {
              "key": {
               "description": "The key to select.",
               "type": "string"
              },
              "name": {
               "description": "Name of the secret.",
               "type": "string"
              },
              "namespace": {
               "description": "Namespace of the secret.",
               "type": "string"
              }
             },
             "required": [
              "key",
              "name",
              "namespace"
             ],
             "type": "object"
            }
           },
           "type": "object"
          },
          "type": "array"
         }
        },
        "required": [
         "enabled"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "autoTuneOptions": {
       "description": "Configuration block for the Auto-Tune options of the domain. Detailed below.",
       "items": {
        "properties": {
         "desiredState": {
          "description": "The Auto-Tune desired state for the domain. Valid values: ENABLED or DISABLED.",
          "type": "string"
         },
         "maintenanceSchedule": {
          "description": "Configuration block for Auto-Tune maintenance windows. Can be specified multiple times for each maintenance window. Detailed below.",
          "items": {
           "properties": {
            "cronExpressionForRecurrence": {
             "description": "A cron expression specifying the recurrence pattern for an Auto-Tune maintenance schedule.",
             "type": "string"
            },
            "duration": {
             "description": "Configuration block for the duration of the Auto-Tune maintenance window. Detailed below.",
             "items": {
              "properties": {
               "unit": {
                "description": "The unit of time specifying the duration of an Auto-Tune maintenance window. Valid values: HOURS.",
                "type": "string"
               },
               "value": {
                "description": "An integer specifying the value of the duration of an Auto-Tune maintenance window.",
                "type": "number"
               }
              },
              "required": [
               "unit",
               "value"
              ],
              "type": "object"
             },
             "type": "array"
            },
            "startAt": {
             "description": "Date and time at which to start the Auto-Tune maintenance schedule in RFC3339 format.",
             "type": "string"
            }
           },
           "required": [
            "cronExpressionForRecurrence",
            "duration",
            "startAt"
           ],
           "type": "object"
          },
          "type": "array"
         },
         "rollbackOnDisable": {
          "description": "Whether to roll back to default Auto-Tune settings when disabling Auto-Tune. Valid values: DEFAULT_ROLLBACK or NO_ROLLBACK.",
          "type": "string"
         }
        },
        "required": [
         "desiredState"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "clusterConfig": {
       "description": "Configuration block for the cluster of the domain. Detailed below.",
       "items": {
        "properties": {
         "coldStorageOptions": {
          "description": "Configuration block containing cold storage configuration. Detailed below.",
          "items": {
           "properties": {
            "enabled": {
             "description": "Boolean to enable cold storage for an Elasticsearch domain. Defaults to false. Master and ultrawarm nodes must be enabled for cold storage.",
             "type": "boolean"
            }
           },
           "type": "object"
          },
          "type": "array"
         },
         "dedicatedMasterCount": {
          "description": "Number of dedicated main nodes in the cluster.",
          "type": "number"
         },
         "dedicatedMasterEnabled": {
          "description": "Whether dedicated main nodes are enabled for the cluster.",
          "type": "boolean"
         },
         "dedicatedMasterType": {
          "description": "Instance type of the dedicated main nodes in the cluster.",
          "type": "string"
         },
         "instanceCount": {
          "description": "Number of instances in the cluster.",
          "type": "number"
         },
         "instanceType": {
          "description": "Instance type of data nodes in the cluster.",
          "type": "string"
         },
         "warmCount": {
          "description": "Number of warm nodes in the cluster. Valid values are between 2 and 150. warm_count can be only and must be set when warm_enabled is set to true.",
          "type": "number"
         },
         "warmEnabled": {
          "description": "Whether to enable warm storage.",
          "type": "boolean"
         },
         "warmType": {
          "description": "Instance type for the Elasticsearch cluster's warm nodes. Valid values are ultrawarm1.medium.elasticsearch, ultrawarm1.large.elasticsearch and ultrawarm1.xlarge.elasticsearch. warm_type can be only and must be set when warm_enabled is set to true.",
          "type": "string"
         },
         "zoneAwarenessConfig": {
          "description": "Configuration block containing zone awareness settings. Detailed below.",
          "items": {
           "properties": {
            "availabilityZoneCount": {
             "description": "Number of Availability Zones for the domain to use with zone_awareness_enabled. Defaults to 2. Valid values: 2 or 3.",
             "type": "number"
            }
           },
           "type": "object"
          },
          "type": "array"
         },
         "zoneAwarenessEnabled": {
          "description": "Whether zone awareness is enabled, set to true for multi-az deployment. To enable awareness with three Availability Zones, the availability_zone_count within the zone_awareness_config must be set to 3.",
          "type": "boolean"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "cognitoOptions": {
       "description": "Configuration block for authenticating Kibana with Cognito. Detailed below.",
       "items": {
        "properties": {
         "enabled": {
          "description": "Whether Amazon Cognito authentication with Kibana is enabled or not.",
          "type": "boolean"
         },
         "identityPoolId": {
          "description": "ID of the Cognito Identity Pool to use.",
          "type": "string"
         },
         "roleArn": {
          "description": "ARN of the IAM role that has the AmazonESCognitoAccess policy attached.",
          "type": "string"
         },
         "userPoolId": {
          "description": "ID of the Cognito User Pool to use.",
          "type": "string"
         }
        },
        "required": [
         "identityPoolId",
         "roleArn",
         "userPoolId"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "domainEndpointOptions": {
       "description": "Configuration block for domain endpoint HTTP(S) related options. Detailed below.",
       "items": {
        "properties": {
         "customEndpoint": {
          "description": "Fully qualified domain for your custom endpoint.",
          "type": "string"
         },
         "customEndpointCertificateArn": {
          "description": "ACM certificate ARN for your custom endpoint.",
          "type": "string"
         },
         "customEndpointEnabled": {
          "description": "Whether to enable custom endpoint for the Elasticsearch domain.",
          "type": "boolean"
         },
         "enforceHttps": {
          "description": "Whether or not to require HTTPS. Defaults to true.",
          "type": "boolean"
         },
         "tlsSecurityPolicy": {
          "description": "Name of the TLS security policy that needs to be applied to the HTTPS endpoint. Valid values:  Policy-Min-TLS-1-0-2019-07 and Policy-Min-TLS-1-2-2019-07.",
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "ebsOptions": {
       "description": "Configuration block for EBS related options, may be required based on chosen instance size. Detailed below.",
       "items": {
        "properties": {
         "ebsEnabled": {
          "description": "Whether EBS volumes are attached to data nodes in the domain.",
          "type": "boolean"
         },
         "iops": {
          "description": "Baseline input/output (I/O) performance of EBS volumes attached to data nodes. Applicable only for the GP3 and Provisioned IOPS EBS volume types.",
          "type": "number"
         },
         "throughput": {
          "description": "Specifies the throughput (in MiB/s) of the EBS volumes attached to data nodes. Applicable only for the gp3 volume type.",
          "type": "number"
         },
         "volumeSize": {
          "description": "Size of EBS volumes attached to data nodes (in GiB).",
          "type": "number"
         },
         "volumeType": {
          "description": "Type of EBS volumes attached to data nodes.",
          "type": "string"
         }
        },
        "required": [
         "ebsEnabled"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "elasticsearchVersion": {
       "description": "Version of Elasticsearch to deploy. Defaults to 1.5.",
       "type": "string"
      },
      "encryptAtRest": {
       "description": "Configuration block for encrypt at rest options. Only available for certain instance types. Detailed below.",
       "items": {
        "properties": {
         "enabled": {
          "description": "Whether to enable encryption at rest. If the encrypt_at_rest block is not provided then this defaults to false. Enabling encryption on new domains requires elasticsearch_version 5.1 or greater.",
          "type": "boolean"
         },
         "kmsKeyId": {
          "description": "KMS key ARN to encrypt the Elasticsearch domain with. If not specified then it defaults to using the aws/es service KMS key. Note that KMS will accept a KMS key ID but will return the key ARN.",
          "type": "string"
         }
        },
        "required": [
         "enabled"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "logPublishingOptions": {
       "description": "Configuration block for publishing slow and application logs to CloudWatch Logs. This block can be declared multiple times, for each log_type, within the same resource. Detailed below.",
       "items": {
        "properties": {
         "cloudwatchLogGroupArn": {
          "description": "ARN of the Cloudwatch log group to which log needs to be published.",
          "type": "string"
         },
         "enabled": {
          "description": "Whether given log publishing option is enabled or not.",
          "type": "boolean"
         },
         "logType": {
          "description": "Type of Elasticsearch log. Valid values: INDEX_SLOW_LOGS, SEARCH_SLOW_LOGS, ES_APPLICATION_LOGS, AUDIT_LOGS.",
          "type": "string"
         }
        },
        "required": [
         "cloudwatchLogGroupArn",
         "logType"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "nodeToNodeEncryption": {
       "description": "Configuration block for node-to-node encryption options. Detailed below.",
       "items": {
        "properties": {
         "enabled": {
          "description": "Whether to enable node-to-node encryption. If the node_to_node_encryption block is not provided then this defaults to false. Enabling node-to-node encryption of a new domain requires an elasticsearch_version of 6.0 or greater.",
          "type": "boolean"
         }
        },
        "required": [
         "enabled"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "region": {
       "description": "Region is the region you'd like your resource to be created in.",
       "type": "string"
      },
      "snapshotOptions": {
       "description": "Configuration block for snapshot related options. Detailed below. DEPRECATED. For domains running Elasticsearch 5.3 and later, Amazon ES takes hourly automated snapshots, making this setting irrelevant. For domains running earlier versions of Elasticsearch, Amazon ES takes daily automated snapshots.",
       "items": {
        "properties": {
         "automatedSnapshotStartHour": {
          "description": "Hour during which the service takes an automated daily snapshot of the indices in the domain.",
          "type": "number"
         }
        },
        "required": [
         "automatedSnapshotStartHour"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "tags": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Map of tags to assign to the resource. If configured with a provider default_tags configuration block present, tags with matching keys will overwrite those defined at the provider-level.",
       "type": "object"
      },
      "tagsAll": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Map of tags assigned to the resource, including those inherited from the provider default_tags configuration block.",
       "type": "object"
      },
      "vpcOptions": {
       "description": "Configuration block for VPC related options. Adding or removing this configuration forces a new resource (documentation). Detailed below.",
       "items": {
        "properties": {
         "securityGroupIds": {
          "description": "List of VPC Security Group IDs to be applied to the Elasticsearch domain endpoints. If omitted, the default Security Group for the VPC will be used.",
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "subnetIds": {
          "description": "List of VPC Subnet IDs for the Elasticsearch domain endpoints to be created in.",
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "managementPolicy": {
     "default": "FullControl",
     "description": "THIS IS AN ALPHA FIELD. Do not use it in production. It is not honored unless the relevant Crossplane feature flag is enabled, and may be changed or removed without notice. ManagementPolicy specifies the level of control Crossplane has over the managed external resource. This field is planned to replace the DeletionPolicy field in a future release. Currently, both could be set independently and non-default values would be honored if the feature flag is enabled. See the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223",
     "enum": [
      "FullControl",
      "ObserveOnly",
      "OrphanOnDelete"
     ],
     "type": "string"
    },
    "providerConfigRef": {
     "default": {
      "name": "default"
     },
     "description": "ProviderConfigReference specifies how the provider that will be used to create, observe, update, and delete this managed resource should be configured.",
     "properties": {
      "name": {
       "description": "Name of the referenced object.",
       "type": "string"
      },
      "policy": {
       "description": "Policies for referencing.",
       "properties": {
        "resolution": {
         "default": "Required",
         "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
         "enum": [
          "Required",
          "Optional"
         ],
         "type": "string"
        },
        "resolve": {
         "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
         "enum": [
          "Always",
          "IfNotPresent"
         ],
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "providerRef": {
     "description": "ProviderReference specifies the provider that will be used to create, observe, update, and delete this managed resource. Deprecated: Please use ProviderConfigReference, i.e. `providerConfigRef`",
     "properties": {
      "name": {
       "description": "Name of the referenced object.",
       "type": "string"
      },
      "policy": {
       "description": "Policies for referencing.",
       "properties": {
        "resolution": {
         "default": "Required",
         "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
         "enum": [
          "Required",
          "Optional"
         ],
         "type": "string"
        },
        "resolve": {
         "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
         "enum": [
          "Always",
          "IfNotPresent"
         ],
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "publishConnectionDetailsTo": {
     "description": "PublishConnectionDetailsTo specifies the connection secret config which contains a name, metadata and a reference to secret store config to which any connection details for this managed resource should be written. Connection details frequently include the endpoint, username, and password required to connect to the managed resource.",
     "properties": {
      "configRef": {
       "default": {
        "name": "default"
       },
       "description": "SecretStoreConfigRef specifies which secret store config should be used for this ConnectionSecret.",
       "properties": {
        "name": {
         "description": "Name of the referenced object.",
         "type": "string"
        },
        "policy": {
         "description": "Policies for referencing.",
         "properties": {
          "resolution": {
           "default": "Required",
           "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
           "enum": [
            "Required",
            "Optional"
           ],
           "type": "string"
          },
          "resolve": {
           "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
           "enum": [
            "Always",
            "IfNotPresent"
           ],
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "required": [
        "name"
       ],
       "type": "object"
      },
      "metadata": {
       "description": "Metadata is the metadata for connection secret.",
       "properties": {
        "annotations": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Annotations are the annotations to be added to connection secret. - For Kubernetes secrets, this will be used as \"metadata.annotations\". - It is up to Secret Store implementation for others store types.",
         "type": "object"
        },
        "labels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Labels are the labels/tags to be added to connection secret. - For Kubernetes secrets, this will be used as \"metadata.labels\". - It is up to Secret Store implementation for others store types.",
         "type": "object"
        },
        "type": {
         "description": "Type is the SecretType for the connection secret. - Only valid for Kubernetes Secret Stores.",
         "type": "string"
        }
       },
       "type": "object"
      },
      "name": {
       "description": "Name is the name of the connection secret.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "writeConnectionSecretToRef": {
     "description": "WriteConnectionSecretToReference specifies the namespace and name of a Secret to which any connection details for this managed resource should be written. Connection details frequently include the endpoint, username, and password required to connect to the managed resource. This field is planned to be replaced in a future release in favor of PublishConnectionDetailsTo. Currently, both could be set independently and connection details would be published to both without affecting each other.",
     "properties": {
      "name": {
       "description": "Name of the secret.",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the secret.",
       "type": "string"
      }
     },
     "required": [
      "name",
      "namespace"
     ],
     "type": "object"
    }
   },
   "required": [
    "forProvider"
   ],
   "type": "object",
   "x-kubernetes-validations": [
    {
     "message": "region is a required parameter",
     "rule": "self.managementPolicy == 'ObserveOnly' || has(self.forProvider.region)"
    }
   ]
  }
 },
 "required": [
  "spec"
 ],
 "title": "Domain",
 "type": "object"
}
