schema:
{
 "description": "Instance is the Schema for the Instances API. Provides an RDS instance resource.",
 "properties": {
  "spec": {
   "description": "InstanceSpec defines the desired state of Instance",
   "properties": {
    "deletionPolicy": {
     "default": "Delete",
     "description": "DeletionPolicy specifies what will happen to the underlying external when this managed resource is deleted - either \"Delete\" or \"Orphan\" the external resource. This field is planned to be deprecated in favor of the ManagementPolicy field in a future release. Currently, both could be set independently and non-default values would be honored if the feature flag is enabled. See the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223",
     "enum": [
      "Orphan",
      "Delete"
     ],
     "type": "string"
    },
    "forProvider": {
     "properties": {
      "allocatedStorage": {
       "description": "The allocated storage in gibibytes. If max_allocated_storage is configured, this argument represents the initial storage allocation and differences from the configuration will be ignored automatically when Storage Autoscaling occurs. If replicate_source_db is set, the value is ignored during the creation of the instance.",
       "type": "number"
      },
      "allowMajorVersionUpgrade": {
       "description": "Indicates that major version upgrades are allowed. Changing this parameter does not result in an outage and the change is asynchronously applied as soon as possible.",
       "type": "boolean"
      },
      "applyImmediately": {
       "description": "Specifies whether any database modifications are applied immediately, or during the next maintenance window. Default is false. See Amazon RDS Documentation for more information.",
       "type": "boolean"
      },
      "autoGeneratePassword": {
       "description": "Password for the master DB user. Note that this may show up in logs, and it will be stored in the state file. Cannot be set if manage_master_user_password is set to true. If true, the password will be auto-generated and stored in the Secret referenced by the passwordSecretRef field.",
       "type": "boolean"
      },
      "autoMinorVersionUpgrade": {
       "description": "Indicates that minor engine upgrades will be applied automatically to the DB instance during the maintenance window. Defaults to true.",
       "type": "boolean"
      },
      "availabilityZone": {
       "description": "The AZ for the RDS instance.",
       "type": "string"
      },
      "backupRetentionPeriod": {
       "description": "The days to retain backups for. Must be between 0 and 35. Default is 0. Must be greater than 0 if the database is used as a source for a Read Replica, uses low-downtime updates, or will use RDS Blue/Green deployments.",
       "type": "number"
      },
      "backupWindow": {
       "description": "The daily time range (in UTC) during which automated backups are created if they are enabled. Example: \"09:46-10:16\". Must not overlap with maintenance_window.",
       "type": "string"
      },
      "blueGreenUpdate": {
       "description": "Enables low-downtime updates using RDS Blue/Green deployments. See blue_green_update below",
       "items": {
        "properties": {
         "enabled": {
          "description": "Enables [low-downtime updates](#Low-Downtime Updates) when true. Default is false.",
          "type": "boolean"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "caCertIdentifier": {
       "description": "The identifier of the CA certificate for the DB instance.",
       "type": "string"
      },
      "characterSetName": {
       "description": "The character set name to use for DB encoding in Oracle and Microsoft SQL instances (collation). This can't be changed. See Oracle Character Sets Supported in Amazon RDS or Server-Level Collation for Microsoft SQL Server for more information.",
       "type": "string"
      },
      "copyTagsToSnapshot": {
       "description": "â€“  Copy all Instance tags to snapshots. Default is false.",
       "type": "boolean"
      },
      "customIamInstanceProfile": {
       "description": "The instance profile associated with the underlying Amazon EC2 instance of an RDS Custom DB instance.",
       "type": "string"
      },
      "customerOwnedIpEnabled": {
       "description": "Indicates whether to enable a customer-owned IP address (CoIP) for an RDS on Outposts DB instance. See CoIP for RDS on Outposts for more information.",
       "type": "boolean"
      },
      "dbName": {
       "description": "The name of the database to create when the DB instance is created. If this parameter is not specified, no database is created in the DB instance. Note that this does not apply for Oracle or SQL Server engines. See the AWS documentation for more details on what applies for those engines. If you are providing an Oracle db name, it needs to be in all upper case. Cannot be specified for a replica.",
       "type": "string"
      },
      "dbSubnetGroupName": {
       "description": "Name of DB subnet group. DB instance will be created in the VPC associated with the DB subnet group. If unspecified, will be created in the default VPC, or in EC2 Classic, if available. When working with read replicas, it should be specified only if the source database specifies an instance in another AWS Region. See DBSubnetGroupName in API action CreateDBInstanceReadReplica for additional read replica contraints.",
       "type": "string"
      },
      "dbSubnetGroupNameRef": {
       "description": "Reference to a SubnetGroup to populate dbSubnetGroupName.",
       "properties": {
        "name": {
         "description": "Name of the referenced object.",
         "type": "string"
        },
        "policy": {
         "description": "Policies for referencing.",
         "properties": {
          "resolution": {
           "default": "Required",
           "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
           "enum": [
            "Required",
            "Optional"
           ],
           "type": "string"
          },
          "resolve": {
           "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
           "enum": [
            "Always",
            "IfNotPresent"
           ],
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "required": [
        "name"
       ],
       "type": "object"
      },
      "dbSubnetGroupNameSelector": {
       "description": "Selector for a SubnetGroup to populate dbSubnetGroupName.",
       "properties": {
        "matchControllerRef": {
         "description": "MatchControllerRef ensures an object with the same controller reference as the selecting object is selected.",
         "type": "boolean"
        },
        "matchLabels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "MatchLabels ensures an object with matching labels is selected.",
         "type": "object"
        },
        "policy": {
         "description": "Policies for selection.",
         "properties": {
          "resolution": {
           "default": "Required",
           "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
           "enum": [
            "Required",
            "Optional"
           ],
           "type": "string"
          },
          "resolve": {
           "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
           "enum": [
            "Always",
            "IfNotPresent"
           ],
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "deleteAutomatedBackups": {
       "description": "Specifies whether to remove automated backups immediately after the DB instance is deleted. Default is true.",
       "type": "boolean"
      },
      "deletionProtection": {
       "description": "If the DB instance should have deletion protection enabled. The database can't be deleted when this value is set to true. The default is false.",
       "type": "boolean"
      },
      "domain": {
       "description": "The ID of the Directory Service Active Directory domain to create the instance in.",
       "type": "string"
      },
      "domainIamRoleName": {
       "description": "The name of the IAM role to be used when making API calls to the Directory Service.",
       "type": "string"
      },
      "enabledCloudwatchLogsExports": {
       "description": "Set of log types to enable for exporting to CloudWatch logs. If omitted, no logs will be exported. Valid values (depending on engine). MySQL and MariaDB: audit, error, general, slowquery. PostgreSQL: postgresql, upgrade. MSSQL: agent , error. Oracle: alert, audit, listener, trace.",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "engine": {
       "description": "The database engine to use. For supported values, see the Engine parameter in [API action CreateDBInstance](https://docs.aws.amazon.com/AmazonRDS/latest/APIReference/API_CreateDBInstance.html). Note that for Amazon Aurora instances the engine must match the [DB Cluster](https://marketplace.upbound.io/providers/upbound/provider-aws/latest/resources/rds.aws.upbound.io/Cluster/v1beta1)'s engine'. For information on the difference between the available Aurora MySQL engines see Comparison in the [Amazon RDS Release Notes](https://docs.aws.amazon.com/AmazonRDS/latest/AuroraMySQLReleaseNotes/Welcome.html).",
       "type": "string"
      },
      "engineVersion": {
       "description": "The engine version to use. If `autoMinorVersionUpgrade` is enabled, you can provide a prefix of the version such as 5.7 (for 5.7.10). The actual engine version used is returned in the attribute `status.atProvider.engineVersionActual`. For supported values, see the EngineVersion parameter in [API action CreateDBInstance](https://docs.aws.amazon.com/AmazonRDS/latest/APIReference/API_CreateDBInstance.html). Note that for Amazon Aurora instances the engine version must match the [DB Cluster](https://marketplace.upbound.io/providers/upbound/provider-aws/latest/resources/rds.aws.upbound.io/Cluster/v1beta1)'s engine version'.",
       "type": "string"
      },
      "finalSnapshotIdentifier": {
       "description": "The name of your final DB snapshot when this DB instance is deleted. Must be provided if skip_final_snapshot is set to false. The value must begin with a letter, only contain alphanumeric characters and hyphens, and not end with a hyphen or contain two consecutive hyphens. Must not be provided when deleting a read replica.",
       "type": "string"
      },
      "iamDatabaseAuthenticationEnabled": {
       "description": "Specifies whether mappings of AWS Identity and Access Management (IAM) accounts to database accounts is enabled.",
       "type": "boolean"
      },
      "instanceClass": {
       "description": "The instance type of the RDS instance.",
       "type": "string"
      },
      "iops": {
       "description": "The amount of provisioned IOPS. Setting this implies a storage_type of \"io1\". Can only be set when storage_type is \"io1\" or \"gp3\". Cannot be specified for gp3 storage if the allocated_storage value is below a per-engine threshold. See the RDS User Guide for details.",
       "type": "number"
      },
      "kmsKeyId": {
       "description": "The ARN for the KMS encryption key. If creating an encrypted replica, set this to the destination KMS ARN.",
       "type": "string"
      },
      "kmsKeyIdRef": {
       "description": "Reference to a Key in kms to populate kmsKeyId.",
       "properties": {
        "name": {
         "description": "Name of the referenced object.",
         "type": "string"
        },
        "policy": {
         "description": "Policies for referencing.",
         "properties": {
          "resolution": {
           "default": "Required",
           "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
           "enum": [
            "Required",
            "Optional"
           ],
           "type": "string"
          },
          "resolve": {
           "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
           "enum": [
            "Always",
            "IfNotPresent"
           ],
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "required": [
        "name"
       ],
       "type": "object"
      },
      "kmsKeyIdSelector": {
       "description": "Selector for a Key in kms to populate kmsKeyId.",
       "properties": {
        "matchControllerRef": {
         "description": "MatchControllerRef ensures an object with the same controller reference as the selecting object is selected.",
         "type": "boolean"
        },
        "matchLabels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "MatchLabels ensures an object with matching labels is selected.",
         "type": "object"
        },
        "policy": {
         "description": "Policies for selection.",
         "properties": {
          "resolution": {
           "default": "Required",
           "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
           "enum": [
            "Required",
            "Optional"
           ],
           "type": "string"
          },
          "resolve": {
           "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
           "enum": [
            "Always",
            "IfNotPresent"
           ],
           "type": "string"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "licenseModel": {
       "description": "License model information for this DB instance.",
       "type": "string"
      },
      "maintenanceWindow": {
       "description": "The window to perform maintenance in. Syntax: \"ddd:hh24:mi-ddd:hh24:mi\". Eg: \"Mon:00:00-Mon:03:00\". See RDS Maintenance Window docs for more information.",
       "type": "string"
      },
      "manageMasterUserPassword": {
       "description": "Set to true to allow RDS to manage the master user password in Secrets Manager. Cannot be set if password is provided.",
       "type": "boolean"
      },
      "masterUserSecretKmsKeyId": {
       "description": "The Amazon Web Services KMS key identifier is the key ARN, key ID, alias ARN, or alias name for the KMS key. To use a KMS key in a different Amazon Web Services account, specify the key ARN or alias ARN. If not specified, the default KMS key for your Amazon Web Services account is used.",
       "type": "string"
      },
      "maxAllocatedStorage": {
       "description": "When configured, the upper limit to which Amazon RDS can automatically scale the storage of the DB instance. Configuring this will automatically ignore differences to allocated_storage. Must be greater than or equal to allocated_storage or 0 to disable Storage Autoscaling.",
       "type": "number"
      },
      "monitoringInterval": {
       "description": "The interval, in seconds, between points when Enhanced Monitoring metrics are collected for the DB instance. To disable collecting Enhanced Monitoring metrics, specify 0. The default is 0. Valid Values: 0, 1, 5, 10, 15, 30, 60.",
       "type": "number"
      },
      "monitoringRoleArn": {
       "description": "The ARN for the IAM role that permits RDS to send enhanced monitoring metrics to CloudWatch Logs. You can find more information on the AWS Documentation what IAM permissions are needed to allow Enhanced Monitoring for RDS Instances.",
       "type": "string"
      },
      "multiAz": {
       "description": "Specifies if the RDS instance is multi-AZ",
       "type": "boolean"
      },
      "ncharCharacterSetName": {
       "description": "The national character set is used in the NCHAR, NVARCHAR2, and NCLOB data types for Oracle instances. This can't be changed. See Oracle Character Sets Supported in Amazon RDS.",
       "type": "string"
      },
      "networkType": {
       "description": "The network type of the DB instance. Valid values: IPV4, DUAL.",
       "type": "string"
      },
      "optionGroupName": {
       "description": "Name of the DB option group to associate.",
       "type": "string"
      },
      "parameterGroupName": {
       "description": "Name of the DB parameter group to associate.",
       "type": "string"
      },
      "passwordSecretRef": {
       "description": "Password for the master DB user. Note that this may show up in logs, and it will be stored in the state file. Cannot be set if manage_master_user_password is set to true. Password for the master DB user. If you set autoGeneratePassword to true, the Secret referenced here will be created or updated with generated password if it does not already contain one.",
       "properties": {
        "key": {
         "description": "The key to select.",
         "type": "string"
        },
        "name": {
         "description": "Name of the secret.",
         "type": "string"
        },
        "namespace": {
         "description": "Namespace of the secret.",
         "type": "string"
        }
       },
       "required": [
        "key",
        "name",
        "namespace"
       ],
       "type": "object"
      },
      "performanceInsightsEnabled": {
       "description": "Specifies whether Performance Insights are enabled. Defaults to false.",
       "type": "boolean"
      },
      "performanceInsightsKmsKeyId": {
       "description": "The ARN for the KMS key to encrypt Performance Insights data. When specifying performance_insights_kms_key_id, performance_insights_enabled needs to be set to true. Once KMS key is set, it can never be changed.",
       "type": "string"
      },
      "performanceInsightsRetentionPeriod": {
       "description": "Amount of time in days to retain Performance Insights data. Valid values are 7, 731 (2 years) or a multiple of 31. When specifying performance_insights_retention_period, performance_insights_enabled needs to be set to true. Defaults to '7'.",
       "type": "number"
      },
      "port": {
       "description": "The port on which the DB accepts connections.",
       "type": "number"
      },
      "publiclyAccessible": {
       "description": "Bool to control if instance is publicly accessible. Default is false.",
       "type": "boolean"
      },
      "region": {
       "description": "Region is the region you'd like your resource to be created in.",
       "type": "string"
      },
      "replicaMode": {
       "description": "Specifies whether the replica is in either mounted or open-read-only mode. This attribute is only supported by Oracle instances. Oracle replicas operate in open-read-only mode unless otherwise specified. See Working with Oracle Read Replicas for more information.",
       "type": "string"
      },
      "replicateSourceDb": {
       "description": "Specifies that this resource is a Replicate database, and to use this value as the source database. This correlates to the identifier of another Amazon RDS Database to replicate (if replicating within a single region) or ARN of the Amazon RDS Database to replicate (if replicating cross-region). Note that if you are creating a cross-region replica of an encrypted database you will also need to specify a kms_key_id. See DB Instance Replication and Working with PostgreSQL and MySQL Read Replicas for more information on using Replication.",
       "type": "string"
      },
      "restoreToPointInTime": {
       "description": "A configuration block for restoring a DB instance to an arbitrary point in time. Requires the identifier argument to be set with the name of the new DB instance to be created. See Restore To Point In Time below for details.",
       "items": {
        "properties": {
         "restoreTime": {
          "description": "The date and time to restore from. Value must be a time in Universal Coordinated Time (UTC) format and must be before the latest restorable time for the DB instance. Cannot be specified with use_latest_restorable_time.",
          "type": "string"
         },
         "sourceDbInstanceAutomatedBackupsArn": {
          "description": "The ARN of the automated backup from which to restore. Required if source_db_instance_identifier or source_dbi_resource_id is not specified.",
          "type": "string"
         },
         "sourceDbInstanceIdentifier": {
          "description": "The identifier of the source DB instance from which to restore. Must match the identifier of an existing DB instance. Required if source_db_instance_automated_backups_arn or source_dbi_resource_id is not specified.",
          "type": "string"
         },
         "sourceDbiResourceId": {
          "description": "The resource ID of the source DB instance from which to restore. Required if source_db_instance_identifier or source_db_instance_automated_backups_arn is not specified.",
          "type": "string"
         },
         "useLatestRestorableTime": {
          "description": "A boolean value that indicates whether the DB instance is restored from the latest backup time. Defaults to false. Cannot be specified with restore_time.",
          "type": "boolean"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "s3Import": {
       "description": "Restore from a Percona Xtrabackup in S3.  See Importing Data into an Amazon RDS MySQL DB Instance",
       "items": {
        "properties": {
         "bucketName": {
          "description": "The bucket name where your backup is stored",
          "type": "string"
         },
         "bucketPrefix": {
          "description": "Can be blank, but is the path to your backup",
          "type": "string"
         },
         "ingestionRole": {
          "description": "Role applied to load the data.",
          "type": "string"
         },
         "sourceEngine": {
          "description": "Source engine for the backup",
          "type": "string"
         },
         "sourceEngineVersion": {
          "description": "Version of the source engine used to make the backup",
          "type": "string"
         }
        },
        "required": [
         "bucketName",
         "ingestionRole",
         "sourceEngine",
         "sourceEngineVersion"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "skipFinalSnapshot": {
       "description": "Determines whether a final DB snapshot is created before the DB instance is deleted. If true is specified, no DBSnapshot is created. If false is specified, a DB snapshot is created before the DB instance is deleted, using the value from final_snapshot_identifier. Default is false.",
       "type": "boolean"
      },
      "snapshotIdentifier": {
       "description": "Specifies whether or not to create this database from a snapshot. This correlates to the snapshot ID you'd find in the RDS console, e.g: rds:production-2015-06-26-06-05.",
       "type": "string"
      },
      "storageEncrypted": {
       "description": "Specifies whether the DB instance is encrypted. Note that if you are creating a cross-region read replica this field is ignored and you should instead declare kms_key_id with a valid ARN. The default is false if not specified.",
       "type": "boolean"
      },
      "storageThroughput": {
       "description": "The storage throughput value for the DB instance. Can only be set when storage_type is \"gp3\". Cannot be specified if the allocated_storage value is below a per-engine threshold. See the RDS User Guide for details.",
       "type": "number"
      },
      "storageType": {
       "description": "One of \"standard\" (magnetic), \"gp2\" (general purpose SSD), \"gp3\" (general purpose SSD that needs iops independently) or \"io1\" (provisioned IOPS SSD). The default is \"io1\" if iops is specified, \"gp2\" if not.",
       "type": "string"
      },
      "tags": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "A map of tags to assign to the resource. If configured with a provider default_tags configuration block present, tags with matching keys will overwrite those defined at the provider-level.",
       "type": "object"
      },
      "tagsAll": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "A map of tags assigned to the resource, including those inherited from the provider default_tags configuration block.",
       "type": "object"
      },
      "timezone": {
       "description": "Time zone of the DB instance. timezone is currently only supported by Microsoft SQL Server. The timezone can only be set on creation. See MSSQL User Guide for more information.",
       "type": "string"
      },
      "username": {
       "description": "Username for the master DB user. Cannot be specified for a replica.",
       "type": "string"
      },
      "vpcSecurityGroupIds": {
       "description": "List of VPC security groups to associate.",
       "items": {
        "type": "string"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "managementPolicy": {
     "default": "FullControl",
     "description": "THIS IS AN ALPHA FIELD. Do not use it in production. It is not honored unless the relevant Crossplane feature flag is enabled, and may be changed or removed without notice. ManagementPolicy specifies the level of control Crossplane has over the managed external resource. This field is planned to replace the DeletionPolicy field in a future release. Currently, both could be set independently and non-default values would be honored if the feature flag is enabled. See the design doc for more information: https://github.com/crossplane/crossplane/blob/499895a25d1a1a0ba1604944ef98ac7a1a71f197/design/design-doc-observe-only-resources.md?plain=1#L223",
     "enum": [
      "FullControl",
      "ObserveOnly",
      "OrphanOnDelete"
     ],
     "type": "string"
    },
    "providerConfigRef": {
     "default": {
      "name": "default"
     },
     "description": "ProviderConfigReference specifies how the provider that will be used to create, observe, update, and delete this managed resource should be configured.",
     "properties": {
      "name": {
       "description": "Name of the referenced object.",
       "type": "string"
      },
      "policy": {
       "description": "Policies for referencing.",
       "properties": {
        "resolution": {
         "default": "Required",
         "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
         "enum": [
          "Required",
          "Optional"
         ],
         "type": "string"
        },
        "resolve": {
         "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
         "enum": [
          "Always",
          "IfNotPresent"
         ],
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "providerRef": {
     "description": "ProviderReference specifies the provider that will be used to create, observe, update, and delete this managed resource. Deprecated: Please use ProviderConfigReference, i.e. `providerConfigRef`",
     "properties": {
      "name": {
       "description": "Name of the referenced object.",
       "type": "string"
      },
      "policy": {
       "description": "Policies for referencing.",
       "properties": {
        "resolution": {
         "default": "Required",
         "description": "Resolution specifies whether resolution of this reference is required. The default is 'Required', which means the reconcile will fail if the reference cannot be resolved. 'Optional' means this reference will be a no-op if it cannot be resolved.",
         "enum": [
          "Required",
          "Optional"
         ],
         "type": "string"
        },
        "resolve": {
         "description": "Resolve specifies when this reference should be resolved. The default is 'IfNotPresent', which will attempt to resolve the reference only when the corresponding field is not present. Use 'Always' to resolve the reference on every reconcile.",
         "enum": [
          "Always",
          "IfNotPresent"
         ],
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "name"
     ],
