schema:
{
 "description": "TenantGateway is the Schema for the tenantgateways API",
 "properties": {
  "spec": {
   "description": "TenantGatewaySpec defines the desired state of TenantGateway",
   "properties": {
    "baseDomain": {
     "description": "BaseDomain is a record to auto generate domain in ingress.",
     "type": "string"
    },
    "configMapData": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "Initial values of the Ingress Controller ConfigMap. Check https://docs.nginx.com/nginx-ingress-controller/configuration/global-configuration/configmap-resource/ for more information about possible values.",
     "nullable": true,
     "type": "object"
    },
    "image": {
     "description": "The image of the Ingress Controller.",
     "properties": {
      "pullPolicy": {
       "description": "The ImagePullPolicy of the image.",
       "enum": [
        "Never",
        "Always",
        "IfNotPresent"
       ],
       "type": "string"
      },
      "repository": {
       "description": "The repository of the image.",
       "type": "string"
      },
      "tag": {
       "description": "The tag (version) of the image.",
       "type": "string"
      }
     },
     "required": [
      "pullPolicy",
      "repository",
      "tag"
     ],
     "type": "object"
    },
    "ingressClass": {
     "description": "IngressClass 用以区分nginx作用域",
     "type": "string"
    },
    "replicas": {
     "description": "Replicas 负载均衡实例数",
     "format": "int32",
     "type": "integer"
    },
    "service": {
     "description": "The service of the Ingress controller.",
     "nullable": true,
     "properties": {
      "extraLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Specifies extra labels of the service.",
       "type": "object"
      }
     },
     "type": "object"
    },
    "tenant": {
     "description": "Tenant 租户名",
     "type": "string"
    },
    "type": {
     "description": "Type 负载均衡类型",
     "type": "string"
    },
    "workload": {
     "description": "The Workload of the Ingress controller.",
     "nullable": true,
     "properties": {
      "extraLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "Specifies extra labels of the workload(deployment) of nginx.",
       "type": "object"
      },
      "resources": {
       "description": "Specifies resource request and limit of the nginx container",
       "properties": {
        "limits": {
         "additionalProperties": {
          "anyOf": [
           {
            "type": "integer"
           },
           {
            "type": "string"
           }
          ],
          "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
          "x-kubernetes-int-or-string": true
         },
         "description": "Limits describes the maximum amount of compute resources allowed. More info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
         "type": "object"
        },
        "requests": {
         "additionalProperties": {
          "anyOf": [
           {
            "type": "integer"
           },
           {
            "type": "string"
           }
          ],
          "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
          "x-kubernetes-int-or-string": true
         },
         "description": "Requests describes the minimum amount of compute resources required. If Requests is omitted for a container, it defaults to Limits if that is explicitly specified, otherwise to an implementation-defined value. More info: https://kubernetes.io/docs/concepts/configuration/manage-resources-containers/",
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "ingressClass",
    "replicas",
    "tenant",
    "type"
   ],
   "type": "object"
  }
 },
 "title": "Tenant Gateway",
 "type": "object"
}
