schema:
{
 "description": "LightrunJavaAgent is the Schema for the lightrunjavaagents API",
 "properties": {
  "spec": {
   "description": "LightrunJavaAgentSpec defines the desired state of LightrunJavaAgent",
   "properties": {
    "agentCliFlags": {
     "description": "Add cli flags to the agent \"-agentpath:/lightrun/agent/lightrun_agent.so=\u003cAgentCliFlags\u003e\" https://docs.lightrun.com/jvm/agent-configuration/#additional-command-line-flags",
     "type": "string"
    },
    "agentConfig": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "Agent configuration to be changed from default values https://docs.lightrun.com/jvm/agent-configuration/#setting-agent-properties-from-the-agentconfig-file",
     "type": "object"
    },
    "agentEnvVarName": {
     "description": "Env variable that will be patched with the -agentpath Common choice is JAVA_TOOL_OPTIONS Depending on the tool used it may vary from JAVA_OPTS to MAVEN_OPTS and CATALINA_OPTS More info can be found here https://docs.lightrun.com/jvm/build-tools/",
     "type": "string"
    },
    "agentName": {
     "description": "Agent name for registration to the server",
     "type": "string"
    },
    "agentTags": {
     "description": "Agent tags that will be shown in the portal / IDE plugin",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "containerSelector": {
     "description": "List of containers that should be patched in the Pod",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "deploymentName": {
     "description": "Name of the Deployment that will be patched",
     "type": "string"
    },
    "initContainer": {
     "properties": {
      "image": {
       "description": "Image of the init container. Image name and tag will define platform and version of the agent",
       "type": "string"
      },
      "sharedVolumeMountPath": {
       "description": "Path in the app container where volume with agent will be mounted",
       "type": "string"
      },
      "sharedVolumeName": {
       "description": "Name of the volume that will be added to pod",
       "type": "string"
      }
     },
     "required": [
      "image",
      "sharedVolumeMountPath",
      "sharedVolumeName"
     ],
     "type": "object"
    },
    "secretName": {
     "description": "Name of the Secret in the same namespace contains lightrun key and conmpany id",
     "type": "string"
    },
    "serverHostname": {
     "description": "Lightrun server hostname that will be used for downloading an agent Key and company id in the secret has to be taken from this server as well",
     "type": "string"
    }
   },
   "required": [
    "agentEnvVarName",
    "agentTags",
    "containerSelector",
    "deploymentName",
    "initContainer",
    "secretName",
    "serverHostname"
   ],
   "type": "object"
  }
 },
 "title": "Lightrun Java Agent",
 "type": "object"
}
