      "storageClassName": {
       "default": "ceph-block",
       "type": "string"
      }
     },
     "type": "object"
    },
    "tls": {
     "properties": {
      "certManager": {
       "properties": {
        "duration": {
         "default": "2160h",
         "type": "string"
        },
        "issuerRef": {
         "properties": {
          "kind": {
           "default": "ClusterIssuer",
           "enum": [
            "Issuer",
            "ClusterIssuer"
           ],
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "required": [
          "name"
         ],
         "type": "object"
        },
        "renewBefore": {
         "default": "360h",
         "type": "string"
