component.schema:
{
 "description": "MongoDBBackup is the Schema for the mongodbbackups API",
 "properties": {
  "spec": {
   "properties": {
    "clusterRef": {
     "properties": {
      "kind": {
       "enum": [
        "MongoDB",
        "MongoDBSharded"
       ],
       "type": "string"
      },
      "name": {
       "type": "string"
      }
     },
     "required": [
      "kind",
      "name"
     ],
     "type": "object"
    },
    "compression": {
     "default": true,
     "type": "boolean"
    },
    "compressionType": {
     "default": "zstd",
     "enum": [
      "gzip",
      "zstd",
      "snappy"
     ],
     "type": "string"
    },
    "storage": {
     "properties": {
      "pvc": {
       "properties": {
        "size": {
         "anyOf": [
          {
           "type": "integer"
          },
          {
           "type": "string"
          }
         ],
         "x-kubernetes-int-or-string": true
        },
        "storageClassName": {
         "type": "string"
        }
       },
       "required": [
        "size"
       ],
       "type": "object"
      },
      "s3": {
       "properties": {
        "bucket": {
         "type": "string"
        },
        "credentialsRef": {
         "properties": {
          "name": {
           "type": "string"
          }
         },
         "type": "object"
        },
        "endpoint": {
         "type": "string"
        },
        "insecureSkipTLS": {
         "default": false,
         "type": "boolean"
        },
        "prefix": {
         "type": "string"
        },
        "region": {
         "type": "string"
        }
       },
       "required": [
        "bucket",
        "credentialsRef"
       ],
       "type": "object"
      },
      "type": {
       "enum": [
        "s3",
        "pvc"
       ],
       "type": "string"
      }
     },
     "required": [
      "type"
     ],
     "type": "object"
    },
    "type": {
     "default": "full",
     "enum": [
      "full",
      "incremental"
     ],
     "type": "string"
    }
   },
   "required": [
    "clusterRef",
    "storage"
   ],
   "type": "object"
  }
 },
 "title": "Mongo DB Backup",
 "type": "object"
}
