component.schema:
{
 "description": "NamespaceConfig is the Schema for the namespaceconfigs API",
 "properties": {
  "spec": {
   "description": "NamespaceConfigSpec defines the desired state of NamespaceConfig There are two selectors: \"labelSelector\", \"annotationSelector\". Selectors are considered in AND, so if multiple are defined they must all be true for a Namespace to be selected.",
   "properties": {
    "annotationSelector": {
     "description": "AnnotationSelector selects Namespaces by annotation.",
     "properties": {
      "matchExpressions": {
       "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
       "items": {
        "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
        "properties": {
         "key": {
          "description": "key is the label key that the selector applies to.",
          "type": "string"
         },
         "operator": {
          "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
          "type": "string"
         },
         "values": {
          "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "required": [
         "key",
         "operator"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "matchLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
       "type": "object"
      }
     },
     "type": "object"
    },
    "labelSelector": {
     "description": "LabelSelector selects Namespaces by label.",
     "properties": {
      "matchExpressions": {
       "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
       "items": {
        "description": "A label selector requirement is a selector that contains values, a key, and an operator that relates the key and values.",
        "properties": {
         "key": {
          "description": "key is the label key that the selector applies to.",
          "type": "string"
         },
         "operator": {
          "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
          "type": "string"
         },
         "values": {
          "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "required": [
         "key",
         "operator"
        ],
        "type": "object"
       },
       "type": "array"
      },
      "matchLabels": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
       "type": "object"
      }
     },
     "type": "object"
    },
    "templates": {
     "description": "Templates these are the templates of the resources to be created when a selected namespace is created/updated",
     "items": {
      "description": "LockedResourceTemplate represents a resource template in go language to be enforced in a LockedResourceController and can be used in a API specification",
      "properties": {
       "excludedPaths": {
        "description": "ExludedPaths are a set of json paths that need not be considered by the LockedResourceReconciler",
        "items": {
         "type": "string"
        },
        "type": "array",
        "x-kubernetes-list-type": "set"
       },
       "objectTemplate": {
        "description": "ObjectTemplate is a goland template. Whne processed, it must resolve to a yaml representation of an API resource",
        "type": "string"
       }
      },
      "required": [
       "objectTemplate"
      ],
      "type": "object"
     },
     "type": "array"
    }
   },
   "type": "object"
  }
 },
 "title": "Namespace Config",
 "type": "object"
}
