{
 "description": "NdbCluster is the Schema for the Ndb CRD API",
 "properties": {
  "spec": {
   "description": "The desired state of a MySQL NDB Cluster.",
   "properties": {
    "dataNode": {
     "description": "DataNode specifies the configuration of the data node running in MySQL Cluster.",
