      "config": {
       "additionalProperties": {
        "anyOf": [
         {
          "type": "integer"
         },
         {
          "type": "string"
         }
        ],
        "x-kubernetes-int-or-string": true
       },
       "description": "Config is a map of default MySQL Cluster Data node configurations.\n\nMore info :\nhttps://dev.mysql.com/doc/refman/8.0/en/mysql-cluster-params-ndbd.html",
