schema:
{
 "description": "L4LB is the Schema for the l4lbs API",
 "properties": {
  "spec": {
   "description": "L4LBSpec defines the desired state of L4LB",
   "properties": {
    "backend": {
     "items": {
      "description": "L4LBBackend .",
      "pattern": "^(([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])\\.){3}([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5]):([1-9]|[1-9][0-9]{1,3}|[1-5][0-9]{4}|6[0-4][0-9]{3}|65[0-4][0-9]{2}|655[0-2][0-9]|6553[0-4])$",
      "type": "string"
     },
     "type": "array"
    },
    "check": {
     "description": "L4LBCheck .",
     "properties": {
      "requestPath": {
       "type": "string"
      },
      "timeout": {
       "type": "integer"
      },
      "type": {
       "enum": [
        "tcp",
        "http",
        "none"
       ],
       "type": "string"
      }
     },
     "type": "object"
    },
    "frontend": {
     "description": "L4LBFrontend .",
     "properties": {
      "ip": {
       "pattern": "^(([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])\\.){3}([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])$",
       "type": "string"
      },
      "port": {
       "maximum": 65534,
       "minimum": 0,
       "type": "integer"
      }
     },
     "required": [
      "port"
     ],
     "type": "object"
    },
    "ownerTenant": {
     "type": "string"
    },
    "protocol": {
     "enum": [
      "tcp",
      "udp"
     ],
     "type": "string"
    },
    "site": {
     "type": "string"
    },
    "state": {
     "description": "INSERT ADDITIONAL SPEC FIELDS - desired state of cluster Important: Run \"make\" to regenerate code after modifying this file",
     "enum": [
      "active",
      "disable"
     ],
     "type": "string"
    }
   },
   "required": [
    "backend",
    "frontend"
   ],
   "type": "object"
  }
 },
 "title": "L4LB",
 "type": "object"
}
