component.schema:
{
 "description": "L4LBMeta is the Schema for the l4lbmeta API",
 "properties": {
  "spec": {
   "description": "L4LBMetaSpec defines the desired state of L4LBMeta",
   "properties": {
    "automatic": {
     "type": "boolean"
    },
    "backendIps": {
     "items": {
      "description": "L4LBMetaBackend .",
      "properties": {
       "ip": {
        "type": "string"
       },
       "maintenance": {
        "type": "boolean"
       },
       "port": {
        "type": "integer"
       }
      },
      "required": [
       "ip",
       "maintenance",
       "port"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "healthCheck": {
     "description": "L4LBMetaHealthCheck .",
     "properties": {
      "HTTP": {
       "description": "L4LBMetaHealthCheckHTTP .",
       "properties": {
        "requestPath": {
         "type": "string"
        },
        "timeOut": {
         "type": "string"
        }
       },
       "required": [
        "requestPath",
        "timeOut"
       ],
       "type": "object"
      },
      "TCP": {
       "description": "L4LBMetaHealthCheckTCP .",
       "properties": {
        "requestPath": {
         "type": "string"
        },
        "timeOut": {
         "type": "string"
        }
       },
       "required": [
        "requestPath",
        "timeOut"
       ],
       "type": "object"
      }
     },
     "type": "object"
    },
    "id": {
     "type": "integer"
    },
    "imported": {
     "type": "boolean"
    },
    "ip": {
     "type": "string"
    },
    "kubenet_info": {
     "type": "string"
    },
    "l4lbGeneration": {
     "format": "int64",
     "type": "integer"
    },
    "l4lbName": {
     "type": "string"
    },
    "port": {
     "type": "integer"
    },
    "protocol": {
     "type": "string"
    },
    "reclaimPolicy": {
     "type": "boolean"
    },
    "siteId": {
     "type": "integer"
    },
    "siteName": {
     "type": "string"
    },
    "status": {
     "type": "string"
    },
    "tenantId": {
     "type": "integer"
    }
   },
   "required": [
    "automatic",
    "backendIps",
    "healthCheck",
    "id",
    "imported",
    "ip",
    "kubenet_info",
    "l4lbGeneration",
    "l4lbName",
    "port",
    "protocol",
    "reclaimPolicy",
    "siteId",
    "siteName",
    "status",
    "tenantId"
   ],
   "type": "object"
  }
 },
 "title": "L4LB Meta",
 "type": "object"
}
