schema:
{
 "description": "NexusRole is the Schema for the nexusroles API.",
 "properties": {
  "spec": {
   "description": "NexusRoleSpec defines the desired state of NexusRole.",
   "properties": {
    "description": {
     "description": "Description of nexus role.",
     "example": "Administrator role",
     "type": "string"
    },
    "id": {
     "description": "ID is the id of the role. ID should be unique across all roles.",
     "example": "nx-admin",
     "maxLength": 512,
     "type": "string",
     "x-kubernetes-validations": [
      {
       "message": "Value is immutable",
       "rule": "self == oldSelf"
      }
     ]
    },
    "name": {
     "description": "Name is the name of the role.",
     "example": "nx-admin",
     "type": "string"
    },
    "nexusRef": {
     "description": "NexusRef is a reference to Nexus custom resource.",
     "properties": {
      "kind": {
       "default": "Nexus",
       "description": "Kind specifies the kind of the Nexus resource.",
       "type": "string"
      },
      "name": {
       "description": "Name specifies the name of the Nexus resource.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "privileges": {
     "description": "Privileges is a list of privileges assigned to role.",
     "example": [
      "nx-all"
     ],
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    }
   },
   "required": [
    "id",
    "name",
    "nexusRef"
   ],
   "type": "object"
  }
 },
 "title": "Nexus Role",
 "type": "object"
}
