schema:
{
 "description": "NexusUser is the Schema for the nexususers API.",
 "properties": {
  "spec": {
   "description": "NexusUserSpec defines the desired state of NexusUser.",
   "properties": {
    "email": {
     "description": "Email is the email address of the user.",
     "example": "john.doe@example",
     "maxLength": 254,
     "type": "string"
    },
    "firstName": {
     "description": "FirstName of the user.",
     "example": "John",
     "type": "string"
    },
    "id": {
     "description": "ID is the username of the user. ID should be unique across all users.",
     "example": "new-user",
     "maxLength": 512,
     "type": "string",
     "x-kubernetes-validations": [
      {
       "message": "Value is immutable",
       "rule": "self == oldSelf"
      }
     ]
    },
    "lastName": {
     "description": "LastName of the user.",
     "example": "Doe",
     "type": "string"
    },
    "nexusRef": {
     "description": "NexusRef is a reference to Nexus custom resource.",
     "properties": {
      "kind": {
       "default": "Nexus",
       "description": "Kind specifies the kind of the Nexus resource.",
       "type": "string"
      },
      "name": {
       "description": "Name specifies the name of the Nexus resource.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "roles": {
     "description": "Roles is a list of roles assigned to user.",
     "example": [
      "nx-admin"
     ],
     "items": {
      "type": "string"
     },
     "minItems": 1,
     "type": "array"
    },
    "secret": {
     "description": "Secret is the reference of the k8s object Secret for the user password. Format: $secret-name:secret-key. Updating user password is not supported.",
     "example": "$nexus-user-secret:password-filed",
     "type": "string"
    },
    "status": {
     "default": "active",
     "description": "Status is a status of the user.",
     "enum": [
      "active",
      "disabled"
     ],
     "example": "active",
     "type": "string"
    }
   },
   "required": [
    "email",
    "firstName",
    "id",
    "lastName",
    "nexusRef",
    "roles",
    "secret"
   ],
   "type": "object"
  }
 },
 "title": "Nexus User",
 "type": "object"
}
