schema:
{
 "description": "MigrationTask represents an migration task",
 "properties": {
  "spec": {
   "description": "Spec i.e. specifications of the MigrationTask",
   "properties": {
    "cstorPool": {
     "description": "MigrateCStorPool contains the details of the cstor pool to be migrated",
     "properties": {
      "rename": {
       "description": "If a CSPC with the same name as SPC already exists then we can rename SPC during migration using Rename",
       "type": "string"
      },
      "spcName": {
       "description": "SPCName contains the name of the storage pool claim to be migrated",
       "type": "string"
      }
     },
     "type": "object"
    },
    "cstorVolume": {
     "description": "MigrateCStorVolume contains the details of the cstor volume to be migrated",
     "properties": {
      "pvName": {
       "description": "PVName contains the name of the pv associated with the cstor volume to be migrated",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "required": [
  "spec"
 ],
 "title": "Migration Task",
 "type": "object"
}
