schema:
{
 "description": "ZFSNode records information about all zfs pools available in a node. In general, the openebs node-agent creates the ZFSNode object \u0026 periodically synchronizing the zfs pools available in the node. ZFSNode has an owner reference pointing to the corresponding node object.",
 "properties": {
  "pools": {
   "items": {
    "description": "Pool specifies attributes of a given zfs pool that exists on the node.",
    "properties": {
     "free": {
      "anyOf": [
       {
        "type": "integer"
       },
       {
        "type": "string"
       }
      ],
      "description": "Free specifies the available capacity of zfs pool.",
      "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
      "x-kubernetes-int-or-string": true
     },
     "name": {
      "description": "Name of the zfs pool.",
      "minLength": 1,
      "type": "string"
     },
     "used": {
      "anyOf": [
       {
        "type": "integer"
       },
       {
        "type": "string"
       }
      ],
      "description": "Used specifies the used capacity of zfs pool.",
      "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
      "x-kubernetes-int-or-string": true
     },
     "uuid": {
      "description": "UUID denotes a unique identity of a zfs pool.",
      "minLength": 1,
      "type": "string"
     }
    },
    "required": [
     "free",
     "used",
     "name",
     "uuid"
    ],
    "type": "object"
   },
   "type": "array"
  }
 },
 "required": [
  "pools"
 ],
 "title": "ZFS Node",
 "type": "object"
}
