component.schema:
{
 "description": "Role is used to define a role for a function",
 "properties": {
  "spec": {
   "description": "RoleSpec maps a number of principals or attributes within a JWT to\na set of policies.",
   "properties": {
    "condition": {
     "additionalProperties": {
      "additionalProperties": {
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "type": "object"
     },
     "description": "Condition is a set of conditions that can be used instead of a principal\nto match against claims within a JWT\n{\n\t\"StringLike\": {\n\t\t\"jwt:https://my-identity-provider.com#sub-id\": [\n\t\t\t\"1234567890\",\n\t\t\t\"0987654321\"\n\t\t],\n\t}\n}",
     "type": "object"
    },
    "policy": {
     "description": "Policy is a list of named policies which apply to this role",
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "principal": {
     "additionalProperties": {
      "items": {
       "type": "string"
      },
      "type": "array"
     },
     "description": "Principal is the principal that the role applies to i.e.\n{\n\t\t\"jwt:sub\":[\"repo:alexellis/minty:ref:refs/heads/master\"]\n}",
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "required": [
  "spec"
 ],
 "title": "Role",
 "type": "object"
}
