schema:
{
 "description": "Plan is the Schema for the plans API",
 "properties": {
  "spec": {
   "description": "PlanSpec defines the desired state of Plan",
   "properties": {
    "apiVersion": {
     "type": "string"
    },
    "backoffLimit": {
     "default": 4,
     "description": "Number of attempts to retry scheduling a lost schedule",
     "type": "integer"
    },
    "checkpointRef": {
     "description": "Enable pushing or fetching checkpoint dates from one of the supported backend",
     "properties": {
      "kind": {
       "type": "string"
      },
      "name": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "cron": {
     "description": "Either corn or interval should be set needs a cron expression",
     "type": "string"
    },
    "dates": {
     "additionalProperties": {
      "properties": {
       "format": {
        "type": "string"
       },
       "offset": {
        "type": "string"
       }
      },
      "required": [
       "format",
       "offset"
      ],
      "type": "object"
     },
     "description": "Templating can be applied before executing the plan Generates dates that will be substituted in .spec.punchline.template",
     "type": "object"
    },
    "implementation": {
     "description": "Will be used in case of Sparkline for executing pyspark/spark",
     "enum": [
      "java",
      "python"
     ],
     "type": "string"
    },
    "interval": {
     "description": "Either corn or interval should be set use 1s, 1m, 1h or 1d",
     "type": "string"
    },
    "kind": {
     "enum": [
      "Flinkline",
      "Stormline",
      "Sparkline",
      "Application"
     ],
     "type": "string"
    },
    "maxIteration": {
     "description": "Stop plan after N iteration",
     "type": "integer"
    },
    "templateSpec": {
     "description": "Application, Sparkline, Stormline or Flinkline (only .spec.dag)",
     "format": "textarea",
     "type": "string"
    }
   },
   "required": [
    "apiVersion",
    "kind",
    "templateSpec"
   ],
   "type": "object"
  }
 },
 "title": "Plan",
 "type": "object"
}
