component.schema:
{
 "description": "Secret is the Schema for the StreamNative Cloud Secret API",
 "properties": {
  "spec": {
   "description": "SecretSpec defines the desired state of StreamNative Cloud Secret",
   "properties": {
    "apiServerRef": {
     "description": "APIServerRef is the reference to the StreamNativeCloudConnection",
     "properties": {
      "name": {
       "default": "",
       "description": "Name of the referent.\nThis field is effectively required, but due to backwards compatibility is\nallowed to be empty. Instances of this type with an empty value here are\nalmost certainly wrong.\nTODO: Add other useful fields. apiVersion, kind, uid?\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names\nTODO: Drop `kubebuilder:default` when controller-gen doesn't need it https://github.com/kubernetes-sigs/kubebuilder/issues/3896.",
       "type": "string"
      }
     },
     "type": "object",
     "x-kubernetes-map-type": "atomic"
    },
    "data": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "the value should be base64 encoded",
     "type": "object"
    },
    "instanceName": {
     "description": "InstanceName is the name of the instance this secret is for (e.g. pulsar-instance)",
     "type": "string"
    },
    "location": {
     "description": "Location is the location of the secret.",
     "type": "string"
    },
    "poolMemberName": {
     "description": "PoolMemberName is the pool member to deploy the secret.",
     "type": "string"
    },
    "secretRef": {
     "description": "SecretRef is the reference to the kubernetes secret\nWhen SecretRef is set, it will be used to fetch the secret data.\nData will be ignored.",
     "properties": {
      "name": {
       "type": "string"
      },
      "namespace": {
       "type": "string"
      }
     },
     "required": [
      "name",
      "namespace"
     ],
     "type": "object"
    },
    "tolerations": {
     "description": "Toleration is the toleration for the secret.",
     "items": {
      "description": "Toleration The workload this Toleration is attached to tolerates any taint that matches\nthe triple \u003ckey,value,effect\u003e using the matching operator \u003coperator\u003e.",
      "properties": {
       "effect": {
        "description": "Effect indicates the taint effect to match. Empty means match all taint effects.\nWhen specified, allowed values are NoSchedule and PreferNoSchedule.",
        "type": "string"
       },
       "key": {
        "description": "Key is the taint key that the toleration applies to. Empty means match all taint keys.\nIf the key is empty, operator must be Exists; this combination means to match all values and all keys.",
        "type": "string"
       },
       "operator": {
        "description": "Operator represents a key's relationship to the value.\nValid operators are Exists and Equal. Defaults to Equal.\nExists is equivalent to wildcard for value, so that a workload can\ntolerate all taints of a particular category.",
        "type": "string"
       },
       "value": {
        "description": "Value is the taint value the toleration matches to.\nIf the operator is Exists, the value should be empty, otherwise just a regular string.",
        "type": "string"
       }
      },
      "type": "object"
     },
     "type": "array",
     "x-kubernetes-list-type": "atomic"
    },
    "type": {
     "description": "Type Used to facilitate programmatic handling of secret data.",
     "type": "string"
    }
   },
   "required": [
    "apiServerRef"
   ],
   "type": "object"
  }
 },
 "title": "Secret",
 "type": "object"
}
