component.schema:
{
 "description": "LBNodeAgent is the top-level custom resource for configuring node\nagents. It contains the usual CRD metadata, and the agent spec and\nstatus.",
 "properties": {
  "spec": {
   "description": "LBNodeAgentSpec configures the node agents.  It will have one Local\nconfiguration to announce service addresses locally. For examples,\nsee the \"config/\" directory in the PureLB source tree.",
   "properties": {
    "local": {
     "description": "LBNodeAgentLocalSpec configures the announcers to announce service\naddresses by configuring the underlying operating\nsystem. LocalInterface is unimplemented but will be optional. If it\nis not provided then the agents will add the service address to\nwhichever interface carries the default route. ExtLBInterface is\nalso unimplemented.",
     "properties": {
      "extlbint": {
       "default": "kube-lb0",
       "description": "ExtLBInterface specifies the name of the interface to use for\nannouncement of non-local routes. This field is optional but the\ndefault is \"kube-lb0\" which works in most cases.",
       "type": "string"
      },
      "localint": {
       "default": "default",
       "description": "LocalInterface allows the user to specify the interface to use\nfor announcement of local addresses. This field is optional but\nthe default is \"default\" which will make PureLB use the interface\nthat has the default route, which works in most cases.",
       "type": "string"
      },
      "sendgarp": {
       "default": false,
       "description": "SendGratuitousARP determines whether or not the node agent should\nsend Gratuitous ARP messages when it adds an IP address to the\nlocal interface. This can be used to alert network equipment that\nthe IP-to-MAC binding has changed.",
       "type": "boolean"
      }
     },
     "required": [
      "sendgarp"
     ],
     "type": "object"
    }
   },
   "required": [
    "local"
   ],
   "type": "object"
  }
 },
 "required": [
  "spec"
 ],
 "title": "LB Node Agent",
 "type": "object"
}
