schema:
{
 "description": "LBNodeAgent is the top-level custom resource for configuring node agents. It contains the usual CRD metadata, and the agent spec and status.",
 "properties": {
  "spec": {
   "description": "LBNodeAgentSpec configures the node agents.  It will have one Local configuration to announce service addresses locally. For examples, see the \"config/\" directory in the PureLB source tree.",
   "properties": {
    "egress": {
     "description": "LBNodeAgentEgressSpec configures the egress announcer to SNAT traffic from service endpoint pods so that it comes from the service's IP address instead of the pod's IP.",
     "properties": {
      "cniInterface": {
       "default": "cni0",
       "description": "CNIInterface specifies the name of the interface to use for local routes, for example, the interface to use for pod CIDR traffic. This field is optional but the default is \"cni0\" which works for the Flannel CNI.",
       "type": "string"
      },
      "dontNAT": {
       "description": "DontNAT specifies the networks that the egress announcer should not NAT. Typically these would be at least the cluster's pod CIDR and service CIDR.",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "routeTableBase": {
       "default": 500,
       "description": "RouteTableBase specifies the lowest (i.e., starting) value that PureLB will use for its egress gateway routing table. This table will be configured on each node that is not an egress point for the service. For local subnets, this table will be set up on the non-winner nodes. \n There's no good way to detect table ID re-use at runtime so PureLB depends on the user to ensure that nothing else is using these tables.",
       "type": "integer"
      }
     },
     "required": [
      "dontNAT"
     ],
     "type": "object"
    },
    "local": {
     "description": "LBNodeAgentLocalSpec configures the announcers to announce service addresses by configuring the underlying operating system. LocalInterface is unimplemented but will be optional. If it is not provided then the agents will add the service address to whichever interface carries the default route. ExtLBInterface is also unimplemented.",
     "properties": {
      "extlbint": {
       "default": "kube-lb0",
       "description": "ExtLBInterface specifies the name of the interface to use for announcement of non-local routes. This field is optional but the default is \"kube-lb0\" which works in most cases.",
       "type": "string"
      },
      "localint": {
       "default": "default",
       "description": "LocalInterface allows the user to specify the interface to use for announcement of local addresses. This field is optional but the default is \"default\" which will make PureLB use the interface that has the default route, which works in most cases.",
       "type": "string"
      },
      "sendgarp": {
       "default": false,
       "description": "SendGratuitousARP determines whether or not the node agent should send Gratuitous ARP messages when it adds an IP address to the local interface. This can be used to alert network equipment that the IP-to-MAC binding has changed.",
       "type": "boolean"
      }
     },
     "required": [
      "sendgarp"
     ],
     "type": "object"
    }
   },
   "required": [
    "local"
   ],
   "type": "object"
  }
 },
 "required": [
  "spec"
 ],
 "title": "LB Node Agent",
 "type": "object"
}
