schema:
{
 "description": "SonarGroup is the Schema for the sonar group API.",
 "properties": {
  "spec": {
   "description": "SonarGroupSpec defines the desired state of SonarGroup.",
   "properties": {
    "description": {
     "description": "Description of sonar group.",
     "example": "Default group for new users",
     "maxLength": 200,
     "type": "string"
    },
    "name": {
     "description": "Name is a group name. Name should be unique across all groups. Do not edit this field after creation. Otherwise, the group will be recreated.",
     "example": "sonar-users",
     "maxLength": 255,
     "type": "string"
    },
    "permissions": {
     "description": "Permissions is a list of permissions assigned to group.",
     "example": [
      "admin",
      "provisioning"
     ],
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "sonarRef": {
     "description": "SonarRef is a reference to Sonar custom resource.",
     "properties": {
      "kind": {
       "default": "Sonar",
       "description": "Kind specifies the kind of the Sonar resource.",
       "type": "string"
      },
      "name": {
       "description": "Name specifies the name of the Sonar resource.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    }
   },
   "required": [
    "name",
    "sonarRef"
   ],
   "type": "object"
  }
 },
 "title": "Sonar Group",
 "type": "object"
}
