schema:
{
 "description": "SonarUser is the Schema for the sonarusers API",
 "properties": {
  "spec": {
   "description": "SonarUserSpec defines the desired state of SonarUser",
   "properties": {
    "email": {
     "description": "Email is a user email.",
     "example": "myname@email.com",
     "maxLength": 100,
     "type": "string"
    },
    "groups": {
     "description": "Groups is a list of groups assigned to user.",
     "example": [
      "sonar-administrators",
      "developers"
     ],
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "login": {
     "description": "Login is a user login. Do not edit this field after creation. Otherwise, the user will be recreated.",
     "example": "myuser",
     "maxLength": 255,
     "minLength": 2,
     "type": "string"
    },
    "name": {
     "description": "Name is a username.",
     "example": "My Name",
     "maxLength": 200,
     "type": "string"
    },
    "permissions": {
     "description": "Permissions is a list of permissions assigned to user.",
     "example": [
      "admin",
      "provisioning"
     ],
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "secret": {
     "description": "Secret is the name of the secret with the user password. It should contain a password field with a user password. User password can't be updated.",
     "example": "sonar-user-password",
     "type": "string"
    },
    "sonarRef": {
     "description": "SonarRef is a reference to Sonar custom resource.",
     "properties": {
      "kind": {
       "default": "Sonar",
       "description": "Kind specifies the kind of the Sonar resource.",
       "type": "string"
      },
      "name": {
       "description": "Name specifies the name of the Sonar resource.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    }
   },
   "required": [
    "login",
    "name",
    "secret",
    "sonarRef"
   ],
   "type": "object"
  }
 },
 "title": "Sonar User",
 "type": "object"
}
