        "performance": {
         "description": "Configuration that affects the backup network and disk usage performance.\n",
         "properties": {
          "downloadConcurrency": {
           "description": "Backup storage may use several concurrent streams to read the data. This parameter configures the number of parallel streams to use. By default, it's set to the minimum between the number of file to read and 10.\n",
           "minimum": 1,
           "type": "integer"
