component.schema:
{
 "description": "Quota is the Schema for the quotas API",
 "properties": {
  "spec": {
   "properties": {
    "limitrange": {
     "description": "LimitRange defines the allocated LimitRange for the namespace inside tenant",
     "properties": {
      "limits": {
       "description": "Limits is the list of LimitRangeItem objects that are enforced.",
       "items": {
        "description": "LimitRangeItem defines a min/max usage limit for any resource that matches on kind.",
        "properties": {
         "default": {
          "additionalProperties": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "description": "Default resource requirement limit value by resource name if resource limit is omitted.",
          "type": "object"
         },
         "defaultRequest": {
          "additionalProperties": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "description": "DefaultRequest is the default resource requirement request value by resource name if resource request is omitted.",
          "type": "object"
         },
         "max": {
          "additionalProperties": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "description": "Max usage constraints on this kind by resource name.",
          "type": "object"
         },
         "maxLimitRequestRatio": {
          "additionalProperties": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "description": "MaxLimitRequestRatio if specified, the named resource must have a request and limit that are both non-zero where limit divided by request is less than or equal to the enumerated value; this represents the max burst for the named resource.",
          "type": "object"
         },
         "min": {
          "additionalProperties": {
           "anyOf": [
            {
             "type": "integer"
            },
            {
             "type": "string"
            }
           ],
           "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
           "x-kubernetes-int-or-string": true
          },
          "description": "Min usage constraints on this kind by resource name.",
          "type": "object"
         },
         "type": {
          "description": "Type of resource that this limit applies to.",
          "type": "string"
         }
        },
        "required": [
         "type"
        ],
        "type": "object"
       },
       "type": "array"
      }
     },
     "required": [
      "limits"
     ],
     "type": "object"
    },
    "resourcequota": {
     "description": "ResourceQuota defines the allocated ResourceQuota for the tenant",
     "properties": {
      "hard": {
       "additionalProperties": {
        "anyOf": [
         {
          "type": "integer"
         },
         {
          "type": "string"
         }
        ],
        "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
        "x-kubernetes-int-or-string": true
       },
       "description": "hard is the set of desired hard limits for each named resource. More info: https://kubernetes.io/docs/concepts/policy/resource-quotas/",
       "type": "object"
      },
      "scopeSelector": {
       "description": "scopeSelector is also a collection of filters like scopes that must match each object tracked by a quota but expressed using ScopeSelectorOperator in combination with possible values. For a resource to match, both scopes AND scopeSelector (if specified in spec), must be matched.",
       "properties": {
        "matchExpressions": {
         "description": "A list of scope selector requirements by scope of the resources.",
         "items": {
          "description": "A scoped-resource selector requirement is a selector that contains values, a scope name, and an operator that relates the scope name and values.",
          "properties": {
           "operator": {
            "description": "Represents a scope's relationship to a set of values. Valid operators are In, NotIn, Exists, DoesNotExist.",
            "type": "string"
           },
           "scopeName": {
            "description": "The name of the scope that the selector applies to.",
            "type": "string"
           },
           "values": {
            "description": "An array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
            "items": {
             "type": "string"
            },
            "type": "array"
           }
          },
          "required": [
           "operator",
           "scopeName"
          ],
          "type": "object"
         },
         "type": "array"
        }
       },
       "type": "object",
       "x-kubernetes-map-type": "atomic"
      },
      "scopes": {
       "description": "A collection of filters that must match each object tracked by a quota. If not specified, the quota matches all objects.",
       "items": {
        "description": "A ResourceQuotaScope defines a filter that must match each object tracked by a quota",
        "type": "string"
       },
       "type": "array"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Quota",
 "type": "object"
}
