component.schema:
{
 "properties": {
  "spec": {
   "properties": {
    "folderID": {
     "description": "FolderID defines the Grafana folderID",
     "format": "int64",
     "type": "integer"
    },
    "grafanaRef": {
     "description": "GrafanaRef defines the grafana app binding name for the GrafanaDashboard",
     "properties": {
      "name": {
       "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/namespaces/",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "model": {
     "format": "textarea",
     "type": "string"
    },
    "overwrite": {
     "description": "Overwrite defines the existing grafanadashboard with the same name(if any) should be overwritten or not",
     "type": "boolean"
    },
    "templatize": {
     "description": "Templatize defines the fields which supports templating in GrafanaDashboard Model json",
     "properties": {
      "datasource": {
       "type": "boolean"
      },
      "title": {
       "type": "boolean"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Grafana Dashboard",
 "type": "object"
}
