component.schema:
{
 "properties": {
  "spec": {
   "description": "The specification of the Kafka MirrorMaker 2 cluster.",
   "properties": {
    "clientRackInitImage": {
     "description": "The image of the init container used for initializing the `client.rack`.",
     "type": "string"
    },
    "image": {
     "description": "The container image used for Kafka Connect pods. If no image name is explicitly specified, it is determined based on the `spec.version` configuration. The image names are specifically mapped to corresponding versions in the Cluster Operator configuration.",
     "type": "string"
    },
    "jmxOptions": {
     "description": "JMX Options.",
     "properties": {
      "authentication": {
       "description": "Authentication configuration for connecting to the JMX port.",
       "properties": {
        "type": {
         "description": "Authentication type. Currently the only supported types are `password`.`password` type creates a username and protected port with no TLS.",
         "enum": [
          "password"
         ],
         "type": "string"
        }
       },
       "required": [
        "type"
       ],
       "type": "object"
      }
     },
     "type": "object"
    },
    "jvmOptions": {
     "description": "JVM Options for pods.",
     "properties": {
      "-XX": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "A map of -XX options to the JVM.",
       "type": "object"
      },
      "-Xms": {
       "description": "-Xms option to to the JVM.",
       "pattern": "^[0-9]+[mMgG]?$",
       "type": "string"
      },
      "-Xmx": {
       "description": "-Xmx option to to the JVM.",
       "pattern": "^[0-9]+[mMgG]?$",
       "type": "string"
      },
      "gcLoggingEnabled": {
       "description": "Specifies whether the Garbage Collection logging is enabled. The default is false.",
       "type": "boolean"
      },
      "javaSystemProperties": {
       "description": "A map of additional system properties which will be passed using the `-D` option to the JVM.",
       "items": {
        "properties": {
         "name": {
          "description": "The system property name.",
          "type": "string"
         },
         "value": {
          "description": "The system property value.",
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "livenessProbe": {
     "description": "Pod liveness checking.",
     "properties": {
      "failureThreshold": {
       "description": "Minimum consecutive failures for the probe to be considered failed after having succeeded. Defaults to 3. Minimum value is 1.",
       "minimum": 1,
       "type": "integer"
      },
      "initialDelaySeconds": {
       "description": "The initial delay before first the health is first checked. Default to 15 seconds. Minimum value is 0.",
       "minimum": 0,
       "type": "integer"
      },
      "periodSeconds": {
       "description": "How often (in seconds) to perform the probe. Default to 10 seconds. Minimum value is 1.",
       "minimum": 1,
       "type": "integer"
      },
      "successThreshold": {
       "description": "Minimum consecutive successes for the probe to be considered successful after having failed. Defaults to 1. Must be 1 for liveness. Minimum value is 1.",
       "minimum": 1,
       "type": "integer"
      },
      "timeoutSeconds": {
       "description": "The timeout for each attempted health check. Default to 5 seconds. Minimum value is 1.",
       "minimum": 1,
       "type": "integer"
      }
     },
     "type": "object"
    },
    "logging": {
     "description": "Logging configuration for Kafka Connect.",
     "properties": {
      "loggers": {
       "additionalProperties": {
        "type": "string"
       },
       "description": "A Map from logger name to logger level.",
       "type": "object"
      },
      "type": {
       "description": "Logging type, must be either 'inline' or 'external'.",
       "enum": [
        "inline",
        "external"
       ],
       "type": "string"
      },
      "valueFrom": {
       "description": "`ConfigMap` entry where the logging configuration is stored. ",
       "properties": {
        "configMapKeyRef": {
         "description": "Reference to the key in the ConfigMap containing the configuration.",
         "properties": {
          "key": {
           "type": "string"
          },
          "name": {
           "type": "string"
          },
          "optional": {
           "type": "boolean"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "type"
     ],
     "type": "object"
    },
    "metricsConfig": {
     "description": "Metrics configuration.",
     "properties": {
      "type": {
       "description": "Metrics type. The supported types are `jmxPrometheusExporter` and `strimziMetricsReporter`. Type `jmxPrometheusExporter` uses the Prometheus JMX Exporter to expose Kafka JMX metrics in Prometheus format through an HTTP endpoint. Type `strimziMetricsReporter` uses the Strimzi Metrics Reporter to directly expose Kafka metrics in Prometheus format through an HTTP endpoint.",
       "enum": [
        "jmxPrometheusExporter",
        "strimziMetricsReporter"
       ],
       "type": "string"
      },
      "valueFrom": {
       "description": "ConfigMap entry where the Prometheus JMX Exporter configuration is stored.",
       "properties": {
        "configMapKeyRef": {
         "description": "Reference to the key in the ConfigMap containing the configuration.",
         "properties": {
          "key": {
           "type": "string"
          },
          "name": {
           "type": "string"
          },
          "optional": {
           "type": "boolean"
          }
         },
         "type": "object"
        }
       },
       "type": "object"
      },
      "values": {
       "description": "Configuration values for the Strimzi Metrics Reporter.",
       "properties": {
        "allowList": {
         "description": "A list of regex patterns to filter the metrics to collect. Should contain at least one element.",
         "items": {
          "type": "string"
         },
         "type": "array"
        }
       },
       "type": "object"
      }
     },
     "required": [
      "type"
     ],
     "type": "object",
     "x-kubernetes-validations": [
      {
       "message": "valueFrom property is required",
       "rule": "self.type != 'jmxPrometheusExporter' || has(self.valueFrom)"
      }
     ]
    },
    "mirrors": {
     "description": "Configuration of the MirrorMaker 2 connectors.",
     "items": {
      "properties": {
       "checkpointConnector": {
        "description": "The specification of the Kafka MirrorMaker 2 checkpoint connector.",
        "properties": {
         "alterOffsets": {
          "description": "Configuration for altering offsets.",
          "properties": {
           "fromConfigMap": {
            "description": "Reference to the ConfigMap where the new offsets are stored.",
            "properties": {
             "name": {
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "required": [
           "fromConfigMap"
          ],
          "type": "object"
         },
         "autoRestart": {
          "description": "Automatic restart of connector and tasks configuration.",
          "properties": {
           "enabled": {
            "description": "Whether automatic restart for failed connectors and tasks should be enabled or disabled.",
            "type": "boolean"
           },
           "maxRestarts": {
            "description": "The maximum number of connector restarts that the operator will try. If the connector remains in a failed state after reaching this limit, it must be restarted manually by the user. Defaults to an unlimited number of restarts.",
            "type": "integer"
           }
          },
          "type": "object"
         },
         "config": {
          "description": "The Kafka Connector configuration. The following properties cannot be set: name, connector.class, tasks.max, connector.plugin.version.",
          "format": "textarea",
          "type": "string"
         },
         "listOffsets": {
          "description": "Configuration for listing offsets.",
          "properties": {
           "toConfigMap": {
            "description": "Reference to the ConfigMap where the list of offsets will be written to.",
            "properties": {
             "name": {
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "required": [
           "toConfigMap"
          ],
          "type": "object"
         },
         "state": {
          "description": "The state the connector should be in. Defaults to running.",
          "enum": [
           "paused",
           "stopped",
           "running"
          ],
          "type": "string"
         },
         "tasksMax": {
          "description": "The maximum number of tasks for the Kafka Connector.",
          "minimum": 1,
          "type": "integer"
         },
         "version": {
          "description": "Desired version or version range to respect when starting the Kafka Connector. This is only supported when using Kafka Connect version 4.1.0 and higher.",
          "type": "string"
         }
        },
        "type": "object"
       },
       "groupsExcludePattern": {
        "description": "A regular expression matching the consumer groups to exclude from mirroring. Comma-separated lists are also supported.",
        "type": "string"
       },
       "groupsPattern": {
        "description": "A regular expression matching the consumer groups to be mirrored. Comma-separated lists are also supported.",
        "type": "string"
       },
       "source": {
        "description": "The source Apache Kafka cluster. The source Kafka cluster is used by the Kafka MirrorMaker 2 connectors.",
        "properties": {
         "alias": {
          "description": "Alias used to reference the Kafka cluster.",
          "pattern": "^[a-zA-Z0-9\\._\\-]{1,100}$",
          "type": "string"
         },
         "authentication": {
          "description": "Authentication configuration for connecting to the cluster.",
          "properties": {
           "certificateAndKey": {
            "description": "Reference to the `Secret` which holds the certificate and private key pair.",
            "properties": {
             "certificate": {
              "description": "The name of the file certificate in the Secret.",
              "type": "string"
             },
             "key": {
              "description": "The name of the private key in the secret. The private key must be in unencrypted PKCS #8 format. For more information, see RFC 5208: https://datatracker.ietf.org/doc/html/rfc5208.",
              "type": "string"
             },
             "secretName": {
              "description": "The name of the Secret containing the certificate.",
              "type": "string"
             }
            },
            "required": [
             "secretName",
             "certificate",
             "key"
            ],
            "type": "object"
           },
           "config": {
            "description": "Configuration for the custom authentication mechanism. Only properties with the `sasl.` and `ssl.keystore.` prefixes are allowed. Specify other options in the regular configuration section of the custom resource.",
            "format": "textarea",
            "type": "string"
           },
           "passwordSecret": {
            "description": "Reference to the `Secret` which holds the password.",
            "properties": {
             "password": {
              "description": "The name of the key in the Secret under which the password is stored.",
              "type": "string"
             },
             "secretName": {
              "description": "The name of the Secret containing the password.",
              "type": "string"
             }
            },
            "required": [
             "secretName",
             "password"
            ],
            "type": "object"
           },
           "sasl": {
            "description": "Enable or disable SASL on this authentication mechanism.",
            "type": "boolean"
           },
           "type": {
            "description": "Specifies the authentication type. Supported types are `tls`, `scram-sha-256`, `scram-sha-512`, `plain`, 'oauth', and `custom`. `tls` uses TLS client authentication and is supported only over TLS connections. `scram-sha-256` and `scram-sha-512` use SASL SCRAM-SHA-256 and SASL SCRAM-SHA-512 authentication, respectively. `plain` uses SASL PLAIN authentication. `oauth` uses SASL OAUTHBEARER authentication. `custom` allows you to configure a custom authentication mechanism. As of Strimzi 0.49.0, `oauth` type is deprecated and will be removed in the `v1` API version. Please use `custom` type instead.",
            "enum": [
             "tls",
             "scram-sha-256",
             "scram-sha-512",
             "plain",
             "custom"
            ],
            "type": "string"
           },
           "username": {
            "description": "Username used for the authentication.",
            "type": "string"
           }
          },
          "required": [
           "type"
          ],
          "type": "object"
         },
         "bootstrapServers": {
          "description": "A comma-separated list of `host:port` pairs for establishing the connection to the Kafka cluster.",
          "type": "string"
         },
         "config": {
          "description": "The MirrorMaker 2 cluster config. Properties with the following prefixes cannot be set: ssl., sasl., security., listeners, plugin.path, rest., bootstrap.servers, consumer.interceptor.classes, producer.interceptor.classes (with the exception of: ssl.endpoint.identification.algorithm, ssl.cipher.suites, ssl.protocol, ssl.enabled.protocols).",
          "format": "textarea",
          "type": "string"
         },
         "tls": {
          "description": "TLS configuration for connecting MirrorMaker 2 connectors to a cluster.",
          "properties": {
           "trustedCertificates": {
            "description": "Trusted certificates for TLS connection.",
            "items": {
             "oneOf": [
              {
               "properties": {
                "certificate": {}
               },
               "required": [
                "certificate"
               ]
              },
              {
               "properties": {
                "pattern": {}
               },
               "required": [
                "pattern"
               ]
              }
             ],
             "properties": {
              "certificate": {
               "description": "The name of the file certificate in the secret.",
               "type": "string"
              },
              "pattern": {
               "description": "Pattern for the certificate files in the secret. Use the link:https://en.wikipedia.org/wiki/Glob_(programming)[_glob syntax_] for the pattern. All files in the secret that match the pattern are used.",
               "type": "string"
              },
              "secretName": {
               "description": "The name of the Secret containing the certificate.",
               "type": "string"
              }
             },
             "required": [
              "secretName"
             ],
             "type": "object"
            },
            "type": "array"
           }
          },
          "type": "object"
         }
        },
        "required": [
         "alias",
         "bootstrapServers"
        ],
        "type": "object"
       },
       "sourceConnector": {
        "description": "The specification of the Kafka MirrorMaker 2 source connector.",
        "properties": {
         "alterOffsets": {
          "description": "Configuration for altering offsets.",
          "properties": {
           "fromConfigMap": {
            "description": "Reference to the ConfigMap where the new offsets are stored.",
            "properties": {
             "name": {
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "required": [
           "fromConfigMap"
          ],
          "type": "object"
         },
         "autoRestart": {
          "description": "Automatic restart of connector and tasks configuration.",
          "properties": {
           "enabled": {
            "description": "Whether automatic restart for failed connectors and tasks should be enabled or disabled.",
            "type": "boolean"
           },
           "maxRestarts": {
            "description": "The maximum number of connector restarts that the operator will try. If the connector remains in a failed state after reaching this limit, it must be restarted manually by the user. Defaults to an unlimited number of restarts.",
            "type": "integer"
           }
          },
          "type": "object"
         },
         "config": {
          "description": "The Kafka Connector configuration. The following properties cannot be set: name, connector.class, tasks.max, connector.plugin.version.",
          "format": "textarea",
          "type": "string"
         },
         "listOffsets": {
          "description": "Configuration for listing offsets.",
          "properties": {
           "toConfigMap": {
            "description": "Reference to the ConfigMap where the list of offsets will be written to.",
            "properties": {
             "name": {
              "type": "string"
             }
            },
            "type": "object"
           }
          },
          "required": [
           "toConfigMap"
          ],
          "type": "object"
         },
         "state": {
          "description": "The state the connector should be in. Defaults to running.",
          "enum": [
           "paused",
           "stopped",
           "running"
          ],
          "type": "string"
         },
         "tasksMax": {
          "description": "The maximum number of tasks for the Kafka Connector.",
          "minimum": 1,
          "type": "integer"
         },
         "version": {
          "description": "Desired version or version range to respect when starting the Kafka Connector. This is only supported when using Kafka Connect version 4.1.0 and higher.",
          "type": "string"
         }
        },
        "type": "object"
       },
       "topicsExcludePattern": {
        "description": "A regular expression matching the topics to exclude from mirroring. Comma-separated lists are also supported.",
        "type": "string"
       },
       "topicsPattern": {
        "description": "A regular expression matching the topics to be mirrored, for example, \"topic1\\|topic2\\|topic3\". Comma-separated lists are also supported.",
        "type": "string"
       }
      },
      "required": [
       "source"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "rack": {
     "description": "Configuration of the node label which will be used as the `client.rack` consumer configuration.",
     "properties": {
      "topologyKey": {
       "description": "A key that matches labels assigned to the Kubernetes cluster nodes. The value of the label is used to set a broker's `broker.rack` config, and the `client.rack` config for Kafka Connect or MirrorMaker 2.",
       "example": "topology.kubernetes.io/zone",
       "type": "string"
      }
     },
     "required": [
      "topologyKey"
     ],
     "type": "object"
    },
    "readinessProbe": {
     "description": "Pod readiness checking.",
     "properties": {
      "failureThreshold": {
       "description": "Minimum consecutive failures for the probe to be considered failed after having succeeded. Defaults to 3. Minimum value is 1.",
       "minimum": 1,
       "type": "integer"
      },
      "initialDelaySeconds": {
       "description": "The initial delay before first the health is first checked. Default to 15 seconds. Minimum value is 0.",
       "minimum": 0,
       "type": "integer"
      },
      "periodSeconds": {
       "description": "How often (in seconds) to perform the probe. Default to 10 seconds. Minimum value is 1.",
       "minimum": 1,
       "type": "integer"
      },
      "successThreshold": {
       "description": "Minimum consecutive successes for the probe to be considered successful after having failed. Defaults to 1. Must be 1 for liveness. Minimum value is 1.",
       "minimum": 1,
       "type": "integer"
      },
      "timeoutSeconds": {
       "description": "The timeout for each attempted health check. Default to 5 seconds. Minimum value is 1.",
       "minimum": 1,
       "type": "integer"
      }
     },
     "type": "object"
    },
    "replicas": {
     "description": "The number of pods in the Kafka Connect group. Required in the `v1` version of the Strimzi API. Defaults to `3` in the `v1beta2` version of the Strimzi API.",
     "type": "integer"
    },
    "resources": {
     "description": "The maximum limits for CPU and memory resources and the requested initial resources.",
     "properties": {
      "claims": {
       "items": {
        "properties": {
         "name": {
          "type": "string"
         },
         "request": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      },
      "limits": {
       "additionalProperties": {
        "anyOf": [
         {
          "type": "integer"
         },
         {
          "type": "string"
         }
        ],
        "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
        "x-kubernetes-int-or-string": true
       },
       "type": "object"
      },
      "requests": {
       "additionalProperties": {
        "anyOf": [
         {
          "type": "integer"
         },
         {
          "type": "string"
         }
        ],
        "pattern": "^(\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))(([KMGTPE]i)|[numkMGTPE]|([eE](\\+|-)?(([0-9]+(\\.[0-9]*)?)|(\\.[0-9]+))))?$",
        "x-kubernetes-int-or-string": true
       },
       "type": "object"
      }
     },
     "type": "object"
    },
    "target": {
     "description": "The target Apache Kafka cluster. The target Kafka cluster is used by the underlying Kafka Connect framework for its internal topics.",
     "properties": {
      "alias": {
       "description": "Alias used to reference the Kafka cluster.",
       "pattern": "^[a-zA-Z0-9\\._\\-]{1,100}$",
       "type": "string"
      },
      "authentication": {
       "description": "Authentication configuration for connecting to the cluster.",
       "properties": {
        "certificateAndKey": {
         "description": "Reference to the `Secret` which holds the certificate and private key pair.",
         "properties": {
          "certificate": {
           "description": "The name of the file certificate in the Secret.",
           "type": "string"
          },
          "key": {
           "description": "The name of the private key in the secret. The private key must be in unencrypted PKCS #8 format. For more information, see RFC 5208: https://datatracker.ietf.org/doc/html/rfc5208.",
           "type": "string"
          },
          "secretName": {
           "description": "The name of the Secret containing the certificate.",
           "type": "string"
          }
         },
         "required": [
          "secretName",
          "certificate",
          "key"
         ],
         "type": "object"
        },
        "config": {
         "description": "Configuration for the custom authentication mechanism. Only properties with the `sasl.` and `ssl.keystore.` prefixes are allowed. Specify other options in the regular configuration section of the custom resource.",
         "format": "textarea",
         "type": "string"
        },
        "passwordSecret": {
         "description": "Reference to the `Secret` which holds the password.",
         "properties": {
          "password": {
           "description": "The name of the key in the Secret under which the password is stored.",
           "type": "string"
          },
          "secretName": {
           "description": "The name of the Secret containing the password.",
           "type": "string"
          }
         },
         "required": [
          "secretName",
          "password"
         ],
         "type": "object"
        },
        "sasl": {
         "description": "Enable or disable SASL on this authentication mechanism.",
         "type": "boolean"
        },
        "type": {
         "description": "Specifies the authentication type. Supported types are `tls`, `scram-sha-256`, `scram-sha-512`, `plain`, 'oauth', and `custom`. `tls` uses TLS client authentication and is supported only over TLS connections. `scram-sha-256` and `scram-sha-512` use SASL SCRAM-SHA-256 and SASL SCRAM-SHA-512 authentication, respectively. `plain` uses SASL PLAIN authentication. `oauth` uses SASL OAUTHBEARER authentication. `custom` allows you to configure a custom authentication mechanism. As of Strimzi 0.49.0, `oauth` type is deprecated and will be removed in the `v1` API version. Please use `custom` type instead.",
         "enum": [
          "tls",
          "scram-sha-256",
          "scram-sha-512",
          "plain",
          "custom"
         ],
         "type": "string"
        },
        "username": {
         "description": "Username used for the authentication.",
         "type": "string"
        }
       },
       "required": [
        "type"
       ],
       "type": "object"
      },
      "bootstrapServers": {
       "description": "A comma-separated list of `host:port` pairs for establishing the connection to the Kafka cluster.",
       "type": "string"
      },
      "config": {
       "description": "The MirrorMaker 2 cluster config. Properties with the following prefixes cannot be set: ssl., sasl., security., listeners, plugin.path, rest., bootstrap.servers, consumer.interceptor.classes, producer.interceptor.classes (with the exception of: ssl.endpoint.identification.algorithm, ssl.cipher.suites, ssl.protocol, ssl.enabled.protocols).",
       "format": "textarea",
       "type": "string"
      },
      "configStorageTopic": {
       "description": "The name of the Kafka topic where connector configurations are stored. Required.",
       "type": "string"
      },
      "groupId": {
       "description": "A unique ID that identifies the Connect cluster group. Required.",
       "type": "string"
      },
      "offsetStorageTopic": {
       "description": "The name of the Kafka topic where source connector offsets are stored. Required.",
       "type": "string"
      },
      "statusStorageTopic": {
       "description": "The name of the Kafka topic where connector and task statuses are stored. Required.",
       "type": "string"
      },
      "tls": {
       "description": "TLS configuration for connecting MirrorMaker 2 connectors to a cluster.",
       "properties": {
        "trustedCertificates": {
         "description": "Trusted certificates for TLS connection.",
         "items": {
          "oneOf": [
           {
            "properties": {
             "certificate": {}
            },
            "required": [
             "certificate"
            ]
           },
           {
            "properties": {
             "pattern": {}
            },
            "required": [
             "pattern"
            ]
           }
          ],
          "properties": {
           "certificate": {
            "description": "The name of the file certificate in the secret.",
            "type": "string"
           },
           "pattern": {
            "description": "Pattern for the certificate files in the secret. Use the link:https://en.wikipedia.org/wiki/Glob_(programming)[_glob syntax_] for the pattern. All files in the secret that match the pattern are used.",
            "type": "string"
           },
           "secretName": {
            "description": "The name of the Secret containing the certificate.",
            "type": "string"
           }
          },
          "required": [
           "secretName"
          ],
          "type": "object"
         },
         "type": "array"
        }
       },
       "type": "object"
      }
     },
