schema:
{
 "description": "TCSClusterIssuer is the Schema for the clusterissuers API",
 "properties": {
  "spec": {
   "description": "TCSIssuerSpec defines the desired state of Issuer",
   "properties": {
    "labels": {
     "additionalProperties": {
      "type": "string"
     },
     "description": "Labels to set for the sub-objects (QuoteAttestation, Secret etc.,) created for this issuer.",
     "type": "object"
    },
    "secretName": {
     "description": "SecretName is the name of the secret object to be created by issuer controller to hold ca certificate",
     "type": "string"
    },
    "selfSign": {
     "default": true,
     "description": "SelfSignCertificate defines weather to generate a self-signed certificate for this CA issuer. When it set false, the CA is expected to get provisioned by an external key server using QuoteAttestation CRD. Default to True.",
     "type": "boolean"
    }
   },
   "type": "object"
  }
 },
 "title": "TCS Cluster Issuer",
 "type": "object"
}
