component.schema:
{
 "description": "HPEReplicationMapping defines the properties of a 3PAR/Primera RCGs",
 "properties": {
  "spec": {
   "description": "HPEReplicationMappingSpec defines the properties listed on an HPEReplicationMapping",
   "properties": {
    "primary_array_details": {
     "properties": {
      "cpg": {
       "type": "string"
      },
      "name": {
       "type": "string"
      },
      "secret": {
       "type": "string"
      },
      "secretNamespace": {
       "type": "string"
      }
     },
     "type": "object"
    },
    "rcg_details": {
     "description": "RCG Details",
     "properties": {
      "rcgnames": {
       "items": {
        "type": "string"
       },
       "type": "array"
      }
     },
     "required": [
      "rcgnames"
     ],
     "type": "object"
    },
    "target_array_details": {
     "properties": {
      "cpg": {
       "type": "string"
      },
      "name": {
       "type": "string"
      },
      "secret": {
       "type": "string"
      },
      "secretNamespace": {
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "primary_array_details",
    "rcg_details",
    "target_array_details"
   ],
   "type": "object"
  }
 },
 "required": [
  "metadata",
  "spec"
 ],
 "title": "HPE Replication Mapping",
 "type": "object"
}
