schema:
{
 "description": "Provider is the Schema for the providers API",
 "properties": {
  "spec": {
   "description": "ProviderSpec defines the desired state of Provider",
   "properties": {
    "address": {
     "description": "HTTP/S webhook address of this provider",
     "pattern": "^(http|https)://",
     "type": "string"
    },
    "certSecretRef": {
     "description": "CertSecretRef can be given the name of a secret containing a PEM-encoded CA certificate (`caFile`)",
     "properties": {
      "name": {
       "description": "Name of the referent.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "channel": {
     "description": "Alert channel for this provider",
     "type": "string"
    },
    "proxy": {
     "description": "HTTP/S address of the proxy",
     "pattern": "^(http|https)://",
     "type": "string"
    },
    "secretRef": {
     "description": "Secret reference containing the provider webhook URL using \"address\" as data key",
     "properties": {
      "name": {
       "description": "Name of the referent.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "suspend": {
     "description": "This flag tells the controller to suspend subsequent events handling. Defaults to false.",
     "type": "boolean"
    },
    "timeout": {
     "description": "Timeout for sending alerts to the provider.",
     "pattern": "^([0-9]+(\\.[0-9]+)?(ms|s|m))+$",
     "type": "string"
    },
    "type": {
     "description": "Type of provider",
     "enum": [
      "slack",
      "discord",
      "msteams",
      "rocket",
      "generic",
      "generic-hmac",
      "github",
      "gitlab",
      "bitbucket",
      "azuredevops",
      "googlechat",
      "webex",
      "sentry",
      "azureeventhub",
      "telegram",
      "lark",
      "matrix",
      "opsgenie",
      "alertmanager",
      "grafana",
      "githubdispatch"
     ],
     "type": "string"
    },
    "username": {
     "description": "Bot username for this provider",
     "type": "string"
    }
   },
   "required": [
    "type"
   ],
   "type": "object"
  }
 },
 "title": "Provider",
 "type": "object"
}
