schema:
{
 "description": "EnvBinding is the Schema for the EnvBinding API",
 "properties": {
  "spec": {
   "description": "A EnvBindingSpec defines the desired state of a EnvBinding.",
   "properties": {
    "appTemplate": {
     "description": "AppTemplate indicates the application template.",
     "type": "object"
    },
    "engine": {
     "description": "ClusterManagementEngine represents a multi-cluster management solution",
     "type": "string"
    },
    "envs": {
     "items": {
      "description": "EnvConfig is the configuration for different environments.",
      "properties": {
       "name": {
        "type": "string"
       },
       "patch": {
        "description": "EnvPatch specify the parameter configuration for different environments",
        "properties": {
         "components": {
          "items": {
           "description": "ApplicationComponent describe the component of application",
           "properties": {
            "dependsOn": {
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "externalRevision": {
             "description": "ExternalRevision specified the component revisionName",
             "type": "string"
            },
            "inputs": {
             "description": "StepInputs defines variable input of WorkflowStep",
             "items": {
              "properties": {
               "from": {
                "type": "string"
               },
               "parameterKey": {
                "type": "string"
               }
              },
              "required": [
               "from",
               "parameterKey"
              ],
              "type": "object"
             },
             "type": "array"
            },
            "name": {
             "type": "string"
            },
            "outputs": {
             "description": "StepOutputs defines output variable of WorkflowStep",
             "items": {
              "properties": {
               "name": {
                "type": "string"
               },
               "valueFrom": {
                "type": "string"
               }
              },
              "required": [
               "name",
               "valueFrom"
              ],
              "type": "object"
             },
             "type": "array"
            },
            "properties": {
             "type": "object"
            },
            "scopes": {
             "additionalProperties": {
              "type": "string"
             },
             "description": "scopes in ApplicationComponent defines the component-level scopes the format is \u003cscope-type:scope-instance-name\u003e pairs, the key represents type of `ScopeDefinition` while the value represent the name of scope instance.",
             "type": "object"
            },
            "traits": {
             "description": "Traits define the trait of one component, the type must be array to keep the order.",
             "items": {
              "description": "ApplicationTrait defines the trait of application",
              "properties": {
               "properties": {
                "type": "object"
               },
               "type": {
                "type": "string"
               }
              },
              "required": [
               "type"
              ],
              "type": "object"
             },
             "type": "array"
            },
            "type": {
             "type": "string"
            }
           },
           "required": [
            "name",
            "type"
           ],
           "type": "object"
          },
          "type": "array"
         }
        },
        "required": [
         "components"
        ],
        "type": "object"
       },
       "placement": {
        "description": "EnvPlacement defines the placement rules for an app.",
        "properties": {
         "clusterSelector": {
          "description": "ClusterSelector defines the rules to select a Cluster resource. Either name or labels is needed.",
          "properties": {
           "labels": {
            "additionalProperties": {
             "type": "string"
            },
            "description": "Labels defines the label selector to select the cluster.",
            "type": "object"
           },
           "name": {
            "description": "Name is the name of the cluster.",
            "type": "string"
           }
          },
          "type": "object"
         },
         "namespaceSelector": {
          "description": "NamespaceSelector defines the rules to select a Namespace resource. Either name or labels is needed.",
          "properties": {
           "labels": {
            "additionalProperties": {
             "type": "string"
            },
            "description": "Labels defines the label selector to select the namespace.",
            "type": "object"
           },
           "name": {
            "description": "Name is the name of the namespace.",
            "type": "string"
           }
          },
          "type": "object"
         }
        },
        "type": "object"
       },
       "selector": {
        "description": "EnvSelector defines which components should this env contains",
        "properties": {
         "components": {
          "items": {
           "type": "string"
          },
          "type": "array"
         }
        },
        "type": "object"
       }
      },
      "required": [
       "name",
       "patch"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "outputResourcesTo": {
     "description": "OutputResourcesTo specifies the namespace and name of a ConfigMap which store the resources rendered after differentiated configuration",
     "properties": {
      "name": {
       "description": "Name of the secret.",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the secret.",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    }
   },
   "required": [
    "appTemplate",
    "envs"
   ],
   "type": "object"
  }
 },
 "title": "Env Binding",
 "type": "object"
}
