component.schema:
{
 "description": "PolicyDefinition is the Schema for the policydefinitions API",
 "properties": {
  "spec": {
   "description": "PolicyDefinitionSpec defines the desired state of PolicyDefinition",
   "properties": {
    "definitionRef": {
     "description": "Reference to the CustomResourceDefinition that defines this trait kind.",
     "properties": {
      "name": {
       "description": "Name of the referenced CustomResourceDefinition.",
       "type": "string"
      },
      "version": {
       "description": "Version indicate which version should be used if CRD has multiple versions by default it will use the first one if not specified",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "manageHealthCheck": {
     "description": "ManageHealthCheck means the policy will handle health checking and skip application controller built-in health checking.",
     "type": "boolean"
    },
    "schematic": {
     "description": "Schematic defines the data format and template of the encapsulation of the policy definition. Only CUE schematic is supported for now.",
     "properties": {
      "cue": {
       "description": "CUE defines the encapsulation in CUE format",
       "properties": {
        "template": {
         "description": "Template defines the abstraction template data of the capability, it will replace the old CUE template in extension field. Template is a required field if CUE is defined in Capability Definition.",
         "type": "string"
        }
       },
       "required": [
        "template"
       ],
       "type": "object"
      },
      "terraform": {
       "description": "Terraform is the struct to describe cloud resources managed by Hashicorp Terraform",
       "properties": {
        "configuration": {
         "description": "Configuration is Terraform Configuration",
         "type": "string"
        },
        "customRegion": {
         "description": "Region is cloud provider's region. It will override the region in the region field of ProviderReference",
         "type": "string"
        },
        "deleteResource": {
         "default": true,
         "description": "DeleteResource will determine whether provisioned cloud resources will be deleted when CR is deleted",
         "type": "boolean"
        },
        "gitCredentialsSecretReference": {
         "description": "GitCredentialsSecretReference specifies the reference to the secret containing the git credentials",
         "properties": {
          "name": {
           "description": "name is unique within a namespace to reference a secret resource.",
           "type": "string"
          },
          "namespace": {
           "description": "namespace defines the space within which the secret name must be unique.",
           "type": "string"
          }
         },
         "type": "object",
         "x-kubernetes-map-type": "atomic"
        },
        "path": {
         "description": "Path is the sub-directory of remote git repository. It's valid when remote is set",
         "type": "string"
        },
        "providerRef": {
         "description": "ProviderReference specifies the reference to Provider",
         "properties": {
          "name": {
           "description": "Name of the referenced object.",
           "type": "string"
          },
          "namespace": {
           "default": "default",
           "description": "Namespace of the referenced object.",
           "type": "string"
          }
         },
         "required": [
          "name"
         ],
         "type": "object"
        },
        "type": {
         "default": "hcl",
         "description": "Type specifies which Terraform configuration it is, HCL or JSON syntax",
         "enum": [
          "hcl",
          "json",
          "remote"
         ],
         "type": "string"
        },
        "writeConnectionSecretToRef": {
         "description": "WriteConnectionSecretToReference specifies the namespace and name of a Secret to which any connection details for this managed resource should be written. Connection details frequently include the endpoint, username, and password required to connect to the managed resource.",
         "properties": {
          "name": {
           "description": "Name of the secret.",
           "type": "string"
          },
          "namespace": {
           "description": "Namespace of the secret.",
           "type": "string"
          }
         },
         "required": [
          "name"
         ],
         "type": "object"
        }
       },
       "required": [
        "configuration"
       ],
       "type": "object"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Policy Definition",
 "type": "object"
}
