component.schema:
{
 "description": "Application is the Schema for the applications API",
 "properties": {
  "spec": {
   "description": "ApplicationSpec is the spec of Application",
   "properties": {
    "components": {
     "items": {
      "description": "ApplicationComponent describe the component of application",
      "properties": {
       "dependsOn": {
        "items": {
         "type": "string"
        },
        "type": "array"
       },
       "externalRevision": {
        "description": "ExternalRevision specified the component revisionName",
        "type": "string"
       },
       "inputs": {
        "description": "StepInputs defines variable input of WorkflowStep",
        "items": {
         "description": "InputItem defines an input variable of WorkflowStep",
         "properties": {
          "from": {
           "type": "string"
          },
          "parameterKey": {
           "type": "string"
          }
         },
         "required": [
          "from"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "name": {
        "type": "string"
       },
       "outputs": {
        "description": "StepOutputs defines output variable of WorkflowStep",
        "items": {
         "description": "OutputItem defines an output variable of WorkflowStep",
         "properties": {
          "name": {
           "type": "string"
          },
          "valueFrom": {
           "type": "string"
          }
         },
         "required": [
          "name",
          "valueFrom"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "properties": {
        "format": "textarea",
        "type": "string"
       },
       "scopes": {
        "additionalProperties": {
         "type": "string"
        },
        "description": "scopes in ApplicationComponent defines the component-level scopes the format is \u003cscope-type:scope-instance-name\u003e pairs, the key represents type of `ScopeDefinition` while the value represent the name of scope instance.",
        "format": "textarea",
        "type": "string"
       },
       "traits": {
        "description": "Traits define the trait of one component, the type must be array to keep the order.",
        "items": {
         "description": "ApplicationTrait defines the trait of application",
         "properties": {
          "properties": {
           "format": "textarea",
           "type": "string"
          },
          "type": {
           "type": "string"
          }
         },
         "required": [
          "type"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "type": {
        "type": "string"
       }
      },
      "required": [
       "name",
       "type"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "policies": {
     "description": "Policies defines the global policies for all components in the app, e.g. security, metrics, gitops, multi-cluster placement rules, etc. Policies are applied after components are rendered and before workflow steps are executed.",
     "items": {
      "description": "AppPolicy defines a global policy for all components in the app.",
      "properties": {
       "name": {
        "description": "Name is the unique name of the policy.",
        "type": "string"
       },
       "properties": {
        "format": "textarea",
        "type": "string"
       },
       "type": {
        "description": "Type is the type of the policy",
        "type": "string"
       }
      },
      "required": [
       "type"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "workflow": {
     "description": "Workflow defines how to customize the control logic. If workflow is specified, Vela won't apply any resource, but provide rendered output in AppRevision. Workflow steps are executed in array order, and each step: - will have a context in annotation. - should mark \"finish\" phase in status.conditions.",
     "properties": {
      "mode": {
       "description": "WorkflowExecuteMode defines the mode of workflow execution",
       "properties": {
        "steps": {
         "description": "Steps is the mode of workflow steps execution",
         "type": "string"
        },
        "subSteps": {
         "description": "SubSteps is the mode of workflow sub steps execution",
         "type": "string"
        }
       },
       "type": "object"
      },
      "ref": {
       "type": "string"
      },
      "steps": {
       "items": {
        "description": "WorkflowStep defines how to execute a workflow step.",
        "properties": {
         "dependsOn": {
          "description": "DependsOn is the dependency of the step",
          "items": {
           "type": "string"
          },
          "type": "array"
         },
         "if": {
          "description": "If is the if condition of the step",
          "type": "string"
         },
         "inputs": {
          "description": "Inputs is the inputs of the step",
          "items": {
           "description": "InputItem defines an input variable of WorkflowStep",
           "properties": {
            "from": {
             "type": "string"
            },
            "parameterKey": {
             "type": "string"
            }
           },
           "required": [
            "from"
           ],
           "type": "object"
          },
          "type": "array"
         },
         "meta": {
          "description": "Meta is the meta data of the workflow step.",
          "properties": {
           "alias": {
            "type": "string"
           }
          },
          "type": "object"
         },
         "mode": {
          "description": "Mode is only valid for sub steps, it defines the mode of the sub steps",
          "nullable": true,
          "type": "string"
         },
         "name": {
          "description": "Name is the unique name of the workflow step.",
          "type": "string"
         },
         "outputs": {
          "description": "Outputs is the outputs of the step",
          "items": {
           "description": "OutputItem defines an output variable of WorkflowStep",
           "properties": {
            "name": {
             "type": "string"
            },
            "valueFrom": {
             "type": "string"
            }
           },
           "required": [
            "name",
            "valueFrom"
           ],
           "type": "object"
          },
          "type": "array"
         },
         "properties": {
          "description": "Properties is the properties of the step",
          "format": "textarea",
          "type": "string"
         },
         "subSteps": {
          "items": {
           "description": "WorkflowStepBase defines the workflow step base",
           "properties": {
            "dependsOn": {
             "description": "DependsOn is the dependency of the step",
             "items": {
              "type": "string"
             },
             "type": "array"
            },
            "if": {
             "description": "If is the if condition of the step",
             "type": "string"
            },
            "inputs": {
             "description": "Inputs is the inputs of the step",
             "items": {
              "description": "InputItem defines an input variable of WorkflowStep",
              "properties": {
               "from": {
                "type": "string"
               },
               "parameterKey": {
                "type": "string"
               }
              },
              "required": [
               "from"
              ],
              "type": "object"
             },
             "type": "array"
            },
            "meta": {
             "description": "Meta is the meta data of the workflow step.",
             "properties": {
              "alias": {
               "type": "string"
              }
             },
             "type": "object"
            },
            "name": {
             "description": "Name is the unique name of the workflow step.",
             "type": "string"
            },
            "outputs": {
             "description": "Outputs is the outputs of the step",
             "items": {
              "description": "OutputItem defines an output variable of WorkflowStep",
              "properties": {
               "name": {
                "type": "string"
               },
               "valueFrom": {
                "type": "string"
               }
              },
              "required": [
               "name",
               "valueFrom"
              ],
              "type": "object"
             },
             "type": "array"
            },
            "properties": {
             "description": "Properties is the properties of the step",
             "format": "textarea",
             "type": "string"
            },
            "timeout": {
             "description": "Timeout is the timeout of the step",
             "type": "string"
            },
            "type": {
             "description": "Type is the type of the workflow step.",
             "type": "string"
            }
           },
           "required": [
            "type"
           ],
           "type": "object"
          },
          "type": "array"
         },
         "timeout": {
          "description": "Timeout is the timeout of the step",
          "type": "string"
         },
         "type": {
          "description": "Type is the type of the workflow step.",
          "type": "string"
         }
        },
        "required": [
         "type"
        ],
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "components"
   ],
   "type": "object"
  }
 },
 "title": "Application",
 "type": "object"
}
