schema:
{
 "description": "EventTrigger is the Schema for the eventtriggers API",
 "properties": {
  "spec": {
   "description": "EventTriggerSpec defines how to find objects that apply, what the match condition and a job template spec that gets created when a match occurs.",
   "properties": {
    "matches": {
     "description": "List of things that must be matched in order for the Job to be created. Multiple matches are combined with AND logic.",
     "items": {
      "description": "ETMatch defines a condition to match that will trigger job creation.",
      "properties": {
       "condition": {
        "description": "Details about a status condition that must match.",
        "properties": {
         "status": {
          "description": "The expected value of the status condition when a match occurs.",
          "type": "string"
         },
         "type": {
          "description": "The name of the status condition to check.",
          "type": "string"
         }
        },
        "required": [
         "status",
         "type"
        ],
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "references": {
     "description": "Objects that this event trigger will apply too.",
     "items": {
      "description": "ETReference is a way to identify an object or set of objects that will be watched.",
      "properties": {
       "object": {
        "description": "A single object, given by GVK + namespace + name, that this event trigger will apply too.",
        "properties": {
         "apiVersion": {
          "description": "The API version of the reference object",
          "type": "string"
         },
         "kind": {
          "description": "The kind of the reference object",
          "type": "string"
         },
         "name": {
          "description": "The name of the reference object. This doesn't have to exist prior to creating the CR.",
          "type": "string"
         },
         "namespace": {
          "description": "The namespace that the reference object exists in.",
          "type": "string"
         }
        },
        "required": [
         "apiVersion",
         "kind",
         "name"
        ],
        "type": "object"
       }
      },
      "type": "object"
     },
     "type": "array"
    },
    "template": {
     "description": "A template of a Job that will get created when the conditions are met for any reference object.",
     "properties": {
      "metadata": {
       "description": "The job's object meta data. At a minimum, the name or generateName must be set.",
       "properties": {
        "annotations": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Annotations is an unstructured key value map stored with a resource that may be set by external tools to store and retrieve arbitrary metadata. They are not queryable and should be preserved when modifying objects.",
         "type": "object"
        },
        "generateName": {
         "description": "GenerateName is an optional prefix, used by the server, to generate a unique name ONLY IF the Name field has not been provided.",
         "type": "string"
        },
        "labels": {
         "additionalProperties": {
          "type": "string"
         },
         "description": "Map of string keys and values that can be used to organize and categorize (scope and select) objects.",
         "type": "object"
        },
        "name": {
         "description": "Name must be unique within a namespace. Can be omitted if GenerateName is provided.",
         "type": "string"
        }
       },
       "type": "object"
      },
      "spec": {
       "description": "Specification of the desired behavior of the job.",
       "format": "textarea",
       "type": "string"
      }
     },
     "required": [
      "metadata",
      "spec"
     ],
     "type": "object"
    }
   },
   "required": [
    "matches",
    "references",
    "template"
   ],
   "type": "object"
  }
 },
 "title": "Event Trigger",
 "type": "object"
}
