component.schema:
{
 "description": "VerticaRestorePointsQuery is the Schema for the verticarestorepointsqueries API",
 "properties": {
  "spec": {
   "description": "VerticaRestorePointsQuerySpec defines the desired state of VerticaRestorePointsQuery",
   "properties": {
    "filterOptions": {
     "description": "Optional parameter that will limit the query to only restore points satisfying provided filter options",
     "properties": {
      "archiveName": {
       "description": "Optional parameter that will limit the query to only restore points from this archive",
       "type": "string"
      },
      "endTimestamp": {
       "description": "Optional parameter that will limit the query to only restore points created at this timestamp or before timestamp;\nthe timestamp can be of date time format or date only format, e.g. \"2006-01-02\", \"2006-01-02 15:04:05\", \"2006-01-02 15:04:05.000000000\";\nthe timestamp is interpreted as in UTC timezone",
       "type": "string"
      },
      "startTimestamp": {
       "description": "Optional parameter that will limit the query to only restore points created at this timestamp or after this timestamp;\nthe timestamp can be of date time format or date only format, e.g. \"2006-01-02\", \"2006-01-02 15:04:05\", \"2006-01-02 15:04:05.000000000\";\nthe timestamp is interpreted as in UTC timezone",
       "type": "string"
      }
     },
     "type": "object"
    },
    "verticaDBName": {
     "description": "The name of the VerticaDB CR that this VerticaRestorePointsQuery is defined for.  The\nVerticaDB object must exist in the same namespace as this object.",
     "type": "string"
    }
   },
   "required": [
    "verticaDBName"
   ],
   "type": "object"
  }
 },
 "title": "Vertica Restore Points Query",
 "type": "object"
}
