component.schema:
{
 "description": "VerticaReplicator is the Schema for the verticareplicators API",
 "properties": {
  "spec": {
   "description": "VerticaReplicatorSpec defines the desired state of VerticaReplicator",
   "properties": {
    "mode": {
     "default": "async",
     "description": "Determines how replication is done. Available options: async, sync",
     "type": "string"
    },
    "source": {
     "description": "Information of the source Vertica database to replicate from",
     "properties": {
      "excludePattern": {
       "description": "A string containing a wildcard pattern of the schemas and/or tables to exclude from the set of tables matched\nby the include pattern. Namespace names must be front-qualified with a period.",
       "type": "string"
      },
      "includePattern": {
       "description": "A string containing a wildcard pattern of the schemas and/or tables to include in the replication.\nNamespace names must be front-qualified with a period.",
       "type": "string"
      },
      "objectName": {
       "description": "The object name we want to copy from the source side. The available types are: namespace, schema, table.\nIf this is omitted, the operator will replicate all namespaces in the source database.",
       "type": "string"
      },
      "passwordSecret": {
       "description": "The password secret for the given UserName is stored in this field. If\nthis field and UserName are omitted, the default is set to the superuser\npassword secret found in the VerticaDB. An empty value indicates the\nabsence of a password.\n\n\nThe secret is assumed to be a Kubernetes (k8s) secret unless a secret\npath reference is specified. In the latter case, the secret is retrieved\nfrom an external secret storage manager.",
       "type": "string"
      },
      "sandboxName": {
       "description": "Specify the sandbox name to establish a connection. If no sandbox name is\nprovided, the system assumes the main cluster of the database.",
       "type": "string"
      },
      "serviceName": {
       "description": "This field allows you to specify the name of the service object that will\nbe used to connect to the database. If you do not specify a name, the\nservice object for the first primary subcluster will be used.",
       "type": "string"
      },
      "userName": {
       "description": "The username to connect to Vertica with. If no username is specified, the\ndatabase's superuser will be assumed. Custom username for source database\nis not supported yet.",
       "type": "string"
      },
      "verticaDB": {
       "description": "Name of an existing VerticaDB",
       "type": "string"
      }
     },
     "required": [
      "verticaDB"
     ],
     "type": "object"
    },
    "target": {
     "description": "Information of the target Vertica database to replicate to",
     "properties": {
      "namespace": {
       "description": "Namespace in the target database to which objects are replicated. The target namespace must have the same shard\ncount as the source namespace in the source cluster. If you do not specify a target namespace, objects are\nreplicated to a namespace with the same name as the source namespace. If no such namespace exists in the target\ncluster, it is created with the same name and shard count as the source namespace. You can only replicate tables\nin the public schema to the default_namespace in the target cluster.",
       "type": "string"
      },
      "passwordSecret": {
       "description": "The password secret for the given UserName is stored in this field. If\nthis field and UserName are omitted, the default is set to the superuser\npassword secret found in the VerticaDB. An empty value indicates the\nabsence of a password.\n\n\nThe secret is assumed to be a Kubernetes (k8s) secret unless a secret\npath reference is specified. In the latter case, the secret is retrieved\nfrom an external secret storage manager.",
       "type": "string"
      },
      "sandboxName": {
       "description": "Specify the sandbox name to establish a connection. If no sandbox name is\nprovided, the system assumes the main cluster of the database.",
       "type": "string"
      },
      "serviceName": {
       "description": "This field allows you to specify the name of the service object that will\nbe used to connect to the database. If you do not specify a name, the\nservice object for the first primary subcluster will be used.",
       "type": "string"
      },
      "userName": {
       "description": "The username to connect to Vertica with. If no username is specified, the\ndatabase's superuser will be assumed. Custom username for source database\nis not supported yet.",
       "type": "string"
      },
      "verticaDB": {
       "description": "Name of an existing VerticaDB",
       "type": "string"
      }
     },
     "required": [
      "verticaDB"
     ],
     "type": "object"
    },
    "tlsConfig": {
     "description": "Optional TLS configuration to use when connecting from the source\ndatabase to the target database.\nIt refers to an existing TLS config that already exists in the source.\nUsing TLS configuration for target database authentication requires the\nsame username to be used for both source and target databases. It also\nrequires security config parameter EnableConnectCredentialForwarding to\nbe enabled on the source database. Custom username for source and target\ndatabases is not supported yet when TLS configuration is used.",
     "type": "string"
    }
   },
   "required": [
    "source",
    "target"
   ],
   "type": "object"
  }
 },
 "title": "Vertica Replicator",
 "type": "object"
}
