     "items": {
      "description": "LocalObjectReference contains enough information to let you locate the\nreferenced object inside the same namespace.",
      "properties": {
       "name": {
        "description": "Name of the referent.\nMore info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names\nTODO: Add other useful fields. apiVersion, kind, uid?",
        "type": "string"
       }
      },
      "type": "object",
      "x-kubernetes-map-type": "atomic"
     },
     "type": "array"
    },
    "initContainers": {
     "description": "InitContainers allows adding initContainers to the pod definition. Those can be used to e.g.\nfetch secrets for injection into the vmSingle configuration from external sources. Any\nerrors during the execution of an initContainer will lead to a restart of the Pod. More info: https://kubernetes.io/docs/concepts/workloads/pods/init-containers/\nUsing initContainers for any use case other then secret fetching is entirely outside the scope\nof what the maintainers will support and by doing so, you accept that this behaviour may break\nat any time without notice.",
     "items": {
      "description": "A single application container that you want to run within a pod.",
      "format": "textarea",
      "required": [
       "name"
      ],
      "type": "string"
     },
     "type": "array"
    },
    "insertPorts": {
     "description": "InsertPorts - additional listen ports for data ingestion.",
     "properties": {
      "graphitePort": {
       "description": "GraphitePort listen port",
       "type": "string"
      },
      "influxPort": {
       "description": "InfluxPort listen port",
       "type": "string"
      },
      "openTSDBHTTPPort": {
       "description": "OpenTSDBHTTPPort for http connections.",
       "type": "string"
      },
      "openTSDBPort": {
       "description": "OpenTSDBPort for tcp and udp listen",
       "type": "string"
      }
     },
     "type": "object"
    },
    "license": {
     "description": "License allows to configure license key to be used for enterprise features.\nUsing license key is supported starting from VictoriaMetrics v1.94.0.\nSee: https://docs.victoriametrics.com/enterprise.html",
     "properties": {
      "key": {
       "description": "Enterprise license key. This flag is available only in VictoriaMetrics enterprise.\nDocumentation - https://docs.victoriametrics.com/enterprise.html\nfor more information, visit https://victoriametrics.com/products/enterprise/ .\nTo request a trial license, go to https://victoriametrics.com/products/enterprise/trial/",
       "type": "string"
      },
      "keyRef": {
       "description": "KeyRef is reference to secret with license key for enterprise features.",
       "properties": {
        "key": {
         "description": "The key of the secret to select from.  Must be a valid secret key.",
         "type": "string"
