    "runtimeClassName": {
     "description": "RuntimeClassName - defines runtime class for kubernetes pod.\nhttps://kubernetes.io/docs/concepts/containers/runtime-class/",
     "type": "string"
    },
    "schedulerName": {
     "description": "SchedulerName - defines kubernetes scheduler name",
     "type": "string"
    },
    "secrets": {
     "description": "Secrets is a list of Secrets in the same namespace as the VMSingle\nobject, which shall be mounted into the VMSingle Pods.",
