schema:
{
 "description": "BGPPeerSpec contains the specification for a BGPPeer resource.",
 "properties": {
  "asNumber": {
   "description": "The AS Number of the peer.",
   "format": "int32",
   "type": "integer"
  },
  "keepOriginalNextHop": {
   "description": "Option to keep the original nexthop field when routes are sent to a BGP Peer. Setting \"true\" configures the selected BGP Peers node to use the \"next hop keep;\" instead of \"next hop self;\"(default) in the specific branch of the Node on \"bird.cfg\".",
   "type": "boolean"
  },
  "node": {
   "description": "The node name identifying the Calico node instance that is peering with this peer. If this is not set, this represents a global peer, i.e. a peer that peers with every node in the deployment.",
   "type": "string"
  },
  "nodeSelector": {
   "description": "Selector for the nodes that should have this peering.  When this is set, the Node field must be empty.",
   "type": "string"
  },
  "peerIP": {
   "description": "The IP address of the peer followed by an optional port number to peer with. If port number is given, format should be `[\u003cIPv6\u003e]:port` or `\u003cIPv4\u003e:\u003cport\u003e` for IPv4. If optional port number is not set, and this peer IP and ASNumber belongs to a calico/node with ListenPort set in BGPConfiguration, then we use that port to peer.",
   "type": "string"
  },
  "peerSelector": {
   "description": "Selector for the remote nodes to peer with.  When this is set, the PeerIP and ASNumber fields must be empty.  For each peering between the local node and selected remote nodes, we configure an IPv4 peering if both ends have NodeBGPSpec.IPv4Address specified, and an IPv6 peering if both ends have NodeBGPSpec.IPv6Address specified.  The remote AS number comes from the remote nodeâ€™s NodeBGPSpec.ASNumber, or the global default if that is not set.",
   "type": "string"
  }
 },
 "required": [
  "asNumber",
  "peerIP"
 ],
 "title": "BGP Peer",
 "type": "object"
}
