schema:
{
 "description": "KeycloakRealmSpec defines the desired state of KeycloakRealm.",
 "properties": {
  "browserFlow": {
   "nullable": true,
   "type": "string"
  },
  "browserSecurityHeaders": {
   "additionalProperties": {
    "type": "string"
   },
   "nullable": true,
   "type": "object"
  },
  "disableCentralIDPMappers": {
   "type": "boolean"
  },
  "id": {
   "nullable": true,
   "type": "string"
  },
  "keycloakOwner": {
   "type": "string"
  },
  "passwordPolicy": {
   "items": {
    "properties": {
     "type": {
      "type": "string"
     },
     "value": {
      "type": "string"
     }
    },
    "required": [
     "type",
     "value"
    ],
    "type": "object"
   },
   "nullable": true,
   "type": "array"
  },
  "realmEventConfig": {
   "nullable": true,
   "properties": {
    "adminEventsDetailsEnabled": {
     "type": "boolean"
    },
    "adminEventsEnabled": {
     "type": "boolean"
    },
    "enabledEventTypes": {
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    },
    "eventsEnabled": {
     "type": "boolean"
    },
    "eventsExpiration": {
     "type": "integer"
    },
    "eventsListeners": {
     "items": {
      "type": "string"
     },
     "nullable": true,
     "type": "array"
    }
   },
   "type": "object"
  },
  "realmName": {
   "type": "string"
  },
  "ssoAutoRedirectEnabled": {
   "nullable": true,
   "type": "boolean"
  },
  "ssoRealmEnabled": {
   "nullable": true,
   "type": "boolean"
  },
  "ssoRealmMappers": {
   "items": {
    "properties": {
     "config": {
      "additionalProperties": {
       "type": "string"
      },
      "nullable": true,
      "type": "object"
     },
     "identityProviderMapper": {
      "type": "string"
     },
     "name": {
      "type": "string"
     }
    },
    "type": "object"
   },
   "nullable": true,
   "type": "array"
  },
  "ssoRealmName": {
   "type": "string"
  },
  "themes": {
   "nullable": true,
   "properties": {
    "accountTheme": {
     "nullable": true,
     "type": "string"
    },
    "adminConsoleTheme": {
     "nullable": true,
     "type": "string"
    },
    "emailTheme": {
     "nullable": true,
     "type": "string"
    },
    "internationalizationEnabled": {
     "nullable": true,
     "type": "boolean"
    },
    "loginTheme": {
     "nullable": true,
     "type": "string"
    }
   },
   "type": "object"
  },
  "users": {
   "items": {
    "properties": {
     "realmRoles": {
      "description": "RealmRoles is a list of roles attached to keycloak user",
      "items": {
       "type": "string"
      },
      "type": "array"
     },
     "username": {
      "description": "Username of keycloak user",
      "type": "string"
     }
    },
    "required": [
     "username"
    ],
    "type": "object"
   },
   "nullable": true,
   "type": "array"
  }
 },
 "required": [
  "realmName"
 ],
 "title": "Keycloak Realm",
 "type": "object"
}
