schema:
{
 "description": "AlertProviderSpec defines the desired state of a AlertProvider.",
 "oneOf": [
  {
   "required": [
    "type",
    "address"
   ]
  },
  {
   "required": [
    "type",
    "secretRef"
   ]
  }
 ],
 "properties": {
  "address": {
   "description": "Hook URL address of this provider",
   "type": "string"
  },
  "channel": {
   "description": "Alert channel for this provider",
   "type": "string"
  },
  "proxy": {
   "description": "Http/s proxy of this provider",
   "type": "string"
  },
  "secretRef": {
   "description": "Kubernetes secret reference containing the provider address",
   "properties": {
    "name": {
     "description": "Name of the Kubernetes secret",
     "type": "string"
    }
   },
   "required": [
    "name"
   ],
   "type": "object"
  },
  "type": {
   "description": "Type of this provider",
   "enum": [
    "slack",
    "msteams",
    "discord",
    "rocket",
    "gchat"
   ],
   "type": "string"
  },
  "username": {
   "description": "Bot username for this provider",
   "type": "string"
  }
 },
 "title": "Alert Provider",
 "type": "object"
}
